INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746201, 2268, 'https://mzamin.com/news.php?news=138315', 'https://mzamin.com/news.php?news=138315', 'সংবাদ', NULL, 'কৃপণ বোলিংয়ে সিলসের কীর্তি', '১৫.৫ ওভার বোলিং করে ১০টিই মেডেন', '১৫.৫ ওভার বোলিং করে ১০টিই মেডেন\n১৫.৫ ওভার বোলিং করে ১০টিই মেডেন। মাত্র ৫ রান দিয়ে শিকার চার উইকেট। জ্যামাইকা টেস্টের প্রথম ইনিংসে এটি জেডন সিলসের বোলিং ফিগার। কৃপণ বোলিংয়ের কারণেই তার এই পারফরম্যান্স আলাদা জায়গা পেয়ে গেছে রেকর্ড বইয়ে। সিলস এই ইনিংসে যা করেছেন, এমন কিছুর নজির টেস্ট ইতিহাসে আর নেই। এক ইনিংসে অন্তত ৯০ ডেলিভারি করেছেন, এমন পেসারদের মধ্যে টেস্ট ইতিহাসের সবচেয়ে মিতব্যয়ী বোলিংয়ের কীর্তি এখন সিলসেরই। ওভারপ্রতি রান দিয়েছেন তিনি ০.৩১।এতদিন এখানে সেরা ছিলেন উমেশ যাদব। ২০১৫ সালে দক্ষিণ আফ্রিকার বিপক্ষে দিল্লিতে তার বোলিং ফিগার ছিল ২১-১৬-৯-৩। ভারতীয় পেসার ওভারপ্রতি রান দিয়েছিলেন ০.৪২। পেসার-স্পিনার বা সব ধরনের বোলার মিলিয়ে এক ইনিংসে অন্তত ৯০টি ডেলিভারি করা বোলারদের মধ্যে সিলসের চেয়ে মিতব্যয়ী বোলিং করেছেন টেস্ট ইতিহাসে কেবল একজনই। রমেশচন্দ্র গঙ্গারাম নাদকার্নি, ক্রিকেটে যিনি পরিচিত বাপু নাদকার্নি নামে।টেস্ট ইতিহাসের সবচেয়ে বিখ্যাত বোলিং ফিগারগুলোর একটি উপহার দিয়েছিলেন ভারতের এই বাঁহাতি স্পিনার ১৯৬৪ সালে। কোনো উইকেট সেই ইনিংসে পাননি তিনি, তবে এক পর্যায়ে মেডেন নিয়েছিলেন টানা ২১ ওভার। চেন্নাইয়ে ইংল্যান্ডের বিপক্ষে তার বোলিং ফিগার ছিল শেষ পর্যন্ত ৩২-২৭-৫-০। সিলসের ৪ উইকেটের সঙ্গে শামার জোসেফের ৩ উইকেট ও অন্যদের দারুণ বোলিংয়ে বাংলাদেশকে প্রথম ইনিংসে ১৬৪ রানে গুটিয়ে দেয় ওয়েস্ট ইন্ডিজ। দিনের খেলা শেষে সিলস বলেন, তার মূল চাওয়া ছিল সতীর্থ বোলারদের কাজ সহজ করে দেয়া। ২৩ বছর বয়সী পেসার বলেন, ‘আমি স্রেফ সঠিক জায়গায় বল রাখার চেষ্টা করে গেছি এবং চাপটা ধরে রাখতে চেয়েছি, যাতে অন্য বোলারদের কাজটা সহজ হয়, তারা যেন নিজেদের মেলে ধরতে পারে এবং দলের জন্য কাজটা করতে পারে। এটা আজকে কাজে লেগে গেছে এবং এই পারফরম্যান্সে আমি সন্তুষ্ট।’', '2024-12-03 00:00:00', '2024-12-03 03:47:39', '[]', 1813, '13b6bd276af8e555b69294219a2c547a');
