INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746203, 2268, 'https://mzamin.com/news.php?news=138312', 'https://mzamin.com/news.php?news=138312', 'সংবাদ', NULL, 'অন্য এক বুবলী', 'চলতি বছরে একাধিক সিনেমা মুক্তি পেয়েছে চিত্রনায়িকা শবনম বুবলীর', 'চলতি বছরে একাধিক সিনেমা মুক্তি পেয়েছে চিত্রনায়িকা শবনম বুবলীর\nচলতি বছরে একাধিক সিনেমা মুক্তি পেয়েছে চিত্রনায়িকা শবনম বুবলীর। মুক্তির অপেক্ষায় রয়েছে তার ৭টি সিনেমা। এ ছবিগুলো নতুন বছরে মুক্তি পাবে। তবে চলতি বছরের শেষের দিকে এসে নতুন সিনেমার কাজ নিয়ে ব্যস্ত হয়ে পড়েছেন এ নায়িকা। গত বেশ কিছুদিন ধরেই চলছে ‘পিনিক’ সিনেমার শুটিং। এটি পরিচালনা করছেন জাহিদ জুয়েল। কক্সবাজার ও রামুতে ছবিটির শুটিং হয়েছে একটি লটের। এখন এফডিসিতে চলছে শুটিং। গতকালও এ ছবির শুটিংয়ে অংশ নিয়েছেন বুবলী ও ছবির নায়ক আদর আজাদ। টানা কয়েকদিন শুটিং চলবে এর। তবে এ ছবির লুক নিয়ে এখনই সামনে আসতে চাচ্ছেন না বুবলী। এতে একেবারেই নয়া লুকে দেখা মিলবে এ নায়িকার। বুবলী বলেন, ‘পিনিক’ ঈদের ছবি। বড় আয়োজনে এর শুটিং হচ্ছে। তাই এখনই লুক রিভিল করতে চাই না। এখানে একেবারেই অন্য এক বুবলীকে আবিষ্কার করতে পারবেন দর্শক, এতটুকু কথা দিচ্ছি। আমি ছবিটির গল্প, নির্মাণসহ সবকিছু নিয়ে অনেক আশাবাদী। এদিকে বুবলী ও আদর আজাদ ছাড়াও এ ছবিতে অভিনয় করেছেন ফজলুর রহমান বাবু, আলী রাজ, আজাদ আবুল কালাম, সমু চৌধুরী, মোমেনা চৌধুরী, শিমুল খান, মাসুম বাশার, সেতু, শরীফ সিরাজ, সীমান্ত, মিতুল, নাফিস আহমেদসহ অনেকে।', '2024-12-03 00:00:00', '2024-12-03 03:47:46', '[\"https://mzamin.com/uploads/news/main/138312_bubly.webp\"]', 1813, 'cf3e2de1da3144e610de9bfdb3f35bdd');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746204, 2268, 'https://mzamin.com/news.php?news=138311', 'https://mzamin.com/news.php?news=138311', 'সংবাদ', NULL, '‘পরিচালনায় আসতে চাই না’', 'ঢাকাই সিনেমার আলোচিত চিত্রনায়িকা অপু বিশ্বাস', 'ঢাকাই সিনেমার আলোচিত চিত্রনায়িকা অপু বিশ্বাস\nঢাকাই সিনেমার আলোচিত চিত্রনায়িকা অপু বিশ্বাস। বর্তমানে তিনি সিনেমার কাজ কম করছেন। এদিকে অভিনয়ের পাশাপাশি গেল বছর প্রযোজক হিসেবেও অভিষেক হয় তার। সরকারি অনুদানের ‘লাল শাড়ি’ সিনেমায় প্রযোজনার পাশাপাশি অভিনয়ও করেন। সম্প্রতি প্রযোজক হিসেবে একটি পুরস্কার পান তিনি। সেই অনুষ্ঠানে সাংবাদিকদের মুখোমুখি হয়ে এই নায়িকা বলেন, আজকের অ্যাওয়ার্ডটা আমার কাছে খুবই স্পেশাল। কারণ ‘লাল শাড়ি’ সিনেমার প্রযোজক হিসেবে অ্যাওয়ার্ডটা পেয়েছি। শিল্পী হিসেবে তো অনেক পুরস্কার পেয়েছি। প্রথমবারের মতো প্রযোজক হিসেবেও পেলাম। সেই জায়গা থেকে খুবই ভালো লাগছে। যারা আমাকে মনোনয়ন করেছে ‘লাল শাড়ি’র প্রযোজক হিসেবে তাদের ধন্যবাদ জানাই। এ সময় সামনে পরিচালনায় দেখা যাবে কিনা জানতে চাইলে অপু বলেন, দীর্ঘ ১৮ বছর ধরে অভিনয় করছি। সেটা আমার কাছে সহজ হয়ে গেছে। মডেলিংটাও অভিনয়েরই একটা অংশ। আর বিজনেসও আমার কাছে সহজ মনে হয়। কিন্তু ডিরেকশন দেয়ার জন্য অনেক বেশি মেধা দরকার। অনেক জ্ঞান দরকার। এত বছরের ক্যারিয়ারে কখনো চিন্তা করিনি যে, পরিচালনা করবো। ভবিষ্যতেও ইচ্ছা নেই। আমার ডিরেক্টররা আমাকে অ্যাকশন কাট বলে অভ্যস্ত, আমিও শুনে অভ্যস্ত। আমি সেভাবেই থাকতে চাই। কখনো পরিচালনায় আসতে চাই না।', '2024-12-03 00:00:00', '2024-12-03 03:47:47', '[\"https://mzamin.com/uploads/news/main/138311_apu(cutout).webp\"]', 1813, '3d0e51bd63d0f3bbbb8f0176c78e542e');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746205, 2268, 'https://mzamin.com/news.php?news=138310', 'https://mzamin.com/news.php?news=138310', 'সংবাদ', NULL, 'আঁচল-অমির ‘দুই চাক্কার সাইকেল’', 'চলতি বছরের জানুয়ারিতে শ্রোতাপ্রিয় সংগীতশিল্পী সৈয়দ অমি’র ‘মাতাল’ গানের ভিডিওতে ভিন্ন লুকে হাজির হয়ে প্রশংসা কুড়ান চিত্রনায়িকা আঁচল', 'চলতি বছরের জানুয়ারিতে শ্রোতাপ্রিয় সংগীতশিল্পী সৈয়দ অমি’র ‘মাতাল’ গানের ভিডিওতে ভিন্ন লুকে হাজির হয়ে প্রশংসা কুড়ান চিত্রনায়িকা আঁচল\nচলতি বছরের জানুয়ারিতে শ্রোতাপ্রিয় সংগীতশিল্পী সৈয়দ অমি’র ‘মাতাল’ গানের ভিডিওতে ভিন্ন লুকে হাজির হয়ে প্রশংসা কুড়ান চিত্রনায়িকা আঁচল। গানটি প্রকাশ্যে আসতেই দর্শক লুফে নেয়। ১০ মাসে গানটির ভিউ ২০ মিলিয়ন। এরপর এই জুটিকে আরও বেশকিছু গান-ভিডিওতে দেখা যায়। তারই ধারাবাহিকতায় বছরের শেষ প্রান্তে এসে আবারো সৈয়দ অমি’র গানে মডেল হয়েছেন আঁচল। গানের শিরোনাম ‘দুই চাক্কার সাইকেল’। সালাউদ্দিন সাগরের কথায় গানটিতে সৈয়দ অমি’র সঙ্গে দ্বৈতভাবে কণ্ঠ দিয়েছেন সানজিদা রিমি। গানটির সুর এ এন ফরহাদ এবং সংগীতায়োজন করেছেন সৈয়দ অমি নিজেই। কোরিওগ্রাফিতে ছিলেন রোহান ও বেল্লাল। মালয়েশিয়া এবং সিলেটের মনোরম লোকেশনে গানটির চিত্র ধারণ হয়েছে। সম্প্রতি সৈয়দ অমি’র ইউটিউব চ্যানেলে মিউজিক ভিডিওটি মুক্তি পেয়েছে। এ প্রসঙ্গে আঁচল বলেন, বছরের শুরুতে আমাদের ‘মাতাল’ গানটি থেকে দারুণ সাড়া পেয়েছিলাম। চিন্তা করছিলাম বছর শেষে ভক্তদের জন্য ভিন্ন কিছু উপহার দেই। সেই ভাবনা থেকে ব্যতিক্রম ঘরানার একটি গান ‘দুই চাক্কার সাইকেল’। গানটি প্রকাশ্যে আসতে না আসতেই দর্শকদের প্রচুর সাড়া ও সাপোর্ট পাচ্ছি। সৈয়দ অমি বলেন, দর্শক-শ্রোতাদের নতুন কিছু দেয়ার চেষ্টা থেকেই এই গানটি নির্মিত হয়েছে। এরইমধ্যে গানটি শ্রোতারা পছন্দ করেছেন। তাদের ভালোবাসায় আমরা মুগ্ধ।', '2024-12-03 00:00:00', '2024-12-03 03:47:48', '[\"https://mzamin.com/uploads/news/main/138310_ome-achol.webp\"]', 1813, 'b713a0bcb5843721df0267b3ab309bd1');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746206, 2268, 'https://mzamin.com/news.php?news=138309', 'https://mzamin.com/news.php?news=138309', 'সংবাদ', NULL, 'ফিরলেন ঐশ্বরিয়া', 'ক’দিন ধরে শোনা যাচ্ছে, বিচ্ছেদের পথে হাঁটছেন অভিষেক বচ্চন ও ঐশ্বরিয়া রায় বচ্চন', 'ক’দিন ধরে শোনা যাচ্ছে, বিচ্ছেদের পথে হাঁটছেন অভিষেক বচ্চন ও ঐশ্বরিয়া রায় বচ্চন\nক’দিন ধরে শোনা যাচ্ছে, বিচ্ছেদের পথে হাঁটছেন অভিষেক বচ্চন ও ঐশ্বরিয়া রায় বচ্চন। যদিও এখনো সেই জল্পনা নিয়ে নীরব এ দম্পতি। সবকিছু ছাপিয়ে ফের কাজে ফিরছেন প্রাক্তন বিশ্বসুন্দরী। রূপটানশিল্পী অ্যাড্রিয়ান জেকবের পোস্ট করা ছবি তেমনই ইঙ্গিত দিয়েছে। অভিনেত্রীর সঙ্গে শুটিং সেট থেকেই একটি ছবি পোস্ট করেন জেকব। সঙ্গে ক্যাপশনে লেখেন, কাজের জায়গায় ঐশ্বরিয়ার সঙ্গে একটা ভালো দিন কাটলো। তবে কাজের বিষয়ে বিস্তারিত কিছু জানাননি তিনি। এই ছবি দেখে নেটপাড়ায় অভিনেত্রীর অনুরাগীরা শুভেচ্ছায় ভরিয়ে দিয়েছেন তাকে। এদিকে আম্বানীপুত্র অনন্তের বিয়ের আসর থেকে শুরু হয় ঐশ্বরিয়া-অভিষেকের বিবাহ বিচ্ছেদের জল্পনা। তবে এই জল্পনার মধ্যে এ-ও শোনা যায়, দম্পতি নাকি ফের পর্দায় জুটি বাঁধছেন। মণি রত্নমের ছবিতে ফের একসঙ্গে দেখা যাবে তাদের। এই গুঞ্জন নতুন করে তাদের বিচ্ছেদ-জল্পনার মোড় ঘুরিয়ে দিয়েছে। ২০০৭ সালে ঠিক বিয়ের আগে মণি রত্নমের ছবি ‘গুরু’তে জুটি বেঁধেছিলেন তারা। দু’জনের রসায়নে মুগ্ধ হয়েছিলেন দর্শক। এরপরে ফের পরিচালকের ছবি ‘রাবণ’-এও একসঙ্গে অভিনয় করেন বচ্চন-দম্পতি। ২০১০ সালে মুক্তি পায় সেই ছবি। ১৪ বছর পরে ফের বচ্চন দম্পতির জন্য একটি চিত্রনাট্য খুঁজে পেয়েছেন মণি রত্নম। তবে এখনো এই নিয়ে কোনো প্রতিক্রিয়া দেননি ঐশ্বরিয়া বা অভিষেক কেউই।', '2024-12-03 00:00:00', '2024-12-03 03:47:48', '[\"https://mzamin.com/uploads/news/main/138309_awishariya2.webp\"]', 1813, 'fe70034a399531c4d03488f09a8f3828');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746207, 2268, 'https://mzamin.com/news.php?news=138308', 'https://mzamin.com/news.php?news=138308', 'সংবাদ', NULL, 'সেরা অভিনেত্রী কারিনা', 'রোববার মুম্বইয়ে অনুষ্ঠিত হয়ে গেল ফিল্মফেয়ার ওটিটি অ্যাওয়ার্ডসের পঞ্চম আসর', 'রোববার মুম্বইয়ে অনুষ্ঠিত হয়ে গেল ফিল্মফেয়ার ওটিটি অ্যাওয়ার্ডসের পঞ্চম আসর\nরোববার মুম্বইয়ে অনুষ্ঠিত হয়ে গেল ফিল্মফেয়ার ওটিটি অ্যাওয়ার্ডসের পঞ্চম আসর। এবারের ফিল্মফেয়ার ওটিটি অ্যাওয়ার্ডে সেরা অভিনেত্রীর পুরস্কার পেয়েছেন কারিনা কাপুর। ‘জানে জান’ ছবিতে অনবদ্য অভিনয়ের জন্য এই পুরস্কার পেয়েছেন তিনি। অন্যদিকে ‘অমর সিং চামকিলা’ ওয়েব ছবিতে কিংবদন্তি গায়ক অমর সিং চামকিলার চরিত্রে অভিনয়ের জন্য সেরা অভিনেতার পুরস্কার জিতেছেন অভিনেতা-গায়ক দিলজিৎ দোসাঞ্জ।', '2024-12-03 00:00:00', '2024-12-03 03:47:49', '[\"https://mzamin.com/uploads/news/main/138308_kareena.webp\"]', 1813, 'd0a2617adc606225c99261820405aff2');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746208, 2268, 'https://mzamin.com/news.php?news=138307', 'https://mzamin.com/news.php?news=138307', 'সংবাদ', NULL, 'নাট্যজন সম্মাননায় ইসলাম উদ্দিন', '‘বুকের ভিতর দারুণ ঝড়, আজ সৃষ্টি-সুখে উল্লাস কর’- এই প্রতিপাদ্য নিয়ে আজ থেকে শুরু হচ্ছে ১৩ দিনব্যাপী ‘ঢাকা বিশ্ববিদ্যালয়ে ১৮তম কেন্দ্রীয় বার্ষিক নাট্যোৎসব’', '‘বুকের ভিতর দারুণ ঝড়, আজ সৃষ্টি-সুখে উল্লাস কর’- এই প্রতিপাদ্য নিয়ে আজ থেকে শুরু হচ্ছে ১৩ দিনব্যাপী ‘ঢাকা বিশ্ববিদ্যালয়ে ১৮তম কেন্দ্রীয় বার্ষিক নাট্যোৎসব’\n‘বুকের ভিতর দারুণ ঝড়, আজ সৃষ্টি-সুখে উল্লাস কর’- এই প্রতিপাদ্য নিয়ে আজ থেকে শুরু হচ্ছে ১৩ দিনব্যাপী ‘ঢাকা বিশ্ববিদ্যালয়ে ১৮তম কেন্দ্রীয় বার্ষিক নাট্যোৎসব’। সন্ধ্যায় নাট্যোৎসবের উদ্বোধন করবেন ঢাকা বিশ্ববিদ্যালয়ের উপাচার্য অধ্যাপক নিয়াজ আহমদ খান। উৎসবে বাংলার দেশজ নাট্যের পালাকার ইসলাম উদ্দিনকে বাংলাদেশের সংস্কৃতিতে তার অবদানের জন্য নাট্যজন সম্মাননা প্রদান করা হবে।', '2024-12-03 00:00:00', '2024-12-03 03:47:50', '[\"https://mzamin.com/uploads/news/main/138307_palakar.webp\"]', 1813, 'a7f34746050bbb98b9dd4a15c3c62a7e');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746209, 2268, 'https://mzamin.com/news.php?news=138306', 'https://mzamin.com/news.php?news=138306', 'সংবাদ', NULL, 'বাপ্পীর কথায় গাইলেন কনা', 'জনপ্রিয় কণ্ঠশিল্পী কনা এবার গাইলেন গীতিকবি মীর আহসান হাবীব বাপ্পীর লেখা গান', 'জনপ্রিয় কণ্ঠশিল্পী কনা এবার গাইলেন গীতিকবি মীর আহসান হাবীব বাপ্পীর লেখা গান\nজনপ্রিয় কণ্ঠশিল্পী কনা এবার গাইলেন গীতিকবি মীর আহসান হাবীব বাপ্পীর লেখা গান। রুবাইয়াত শামীম চৌধুরীর সুরে ‘মন থেকে যখন দিয়েছো ফেলে’ গানটির সংগীতায়োজন করেছেন ইব্‌নে রাজন। গান প্রসঙ্গে কনা বলেন, গানটি রেকর্ড করা হয়েছে বাংলাদেশ বেতারের জন্য। বেতারের জন্য গাইতে পারা সবসময়ই অন্যরকম আনন্দের। আশা করছি, নতুন এ গানটি সবার ভালো লাগবে।', '2024-12-03 00:00:00', '2024-12-03 03:47:51', '[\"https://mzamin.com/uploads/news/main/138306_Untitled-3.webp\"]', 1813, '4d52324432ad8651675432608210b997');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746210, 2268, 'https://mzamin.com/news.php?news=138304', 'https://mzamin.com/news.php?news=138304', 'সংবাদ', NULL, 'অভিনেত্রীর রহস্যজনক মৃত্যু', 'রহস্যজনক মৃত্যু হয়েছে কন্নড় সিনেমা ও টেলিভিশনের অভিনেত্রী শোভিতা শিবন্নার', 'রহস্যজনক মৃত্যু হয়েছে কন্নড় সিনেমা ও টেলিভিশনের অভিনেত্রী শোভিতা শিবন্নার\nরহস্যজনক মৃত্যু হয়েছে কন্নড় সিনেমা ও টেলিভিশনের অভিনেত্রী শোভিতা শিবন্নার। গত রোববার হায়দরাবাদের নিজ বাড়ি থেকে উদ্ধার করা হয়েছে তার মরদেহ। তাকে ঝুলন্ত অবস্থায় উদ্ধার হয়। প্রাথমিকভাবে এই মৃত্যুর ঘটনা আত্মহত্যা বলেই অনুমান করছে পুলিশ। তবে কী কারণে এমন পথ বেছে নিলেন অভিনেত্রী তা এখনো জানা যায়নি। ইতিমধ্যেই অভিনেত্রীর আকস্মিক মৃত্যুর তদন্ত শুরু করেছে পুলিশ।', '2024-12-03 00:00:00', '2024-12-03 03:47:52', '[\"https://mzamin.com/uploads/news/main/138304_shovita.webp\"]', 1813, 'ac3ce5fd005328c5de77e8becc77de3b');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746211, 2268, 'https://mzamin.com/news.php?news=138299', 'https://mzamin.com/news.php?news=138299', 'সংবাদ', NULL, 'ফিলিপাইনে ভাইস প্রেসিডেন্টের বিরুদ্ধে অভিশংসন প্রস্তাব', 'সাবেক প্রেসিডেন্ট রড্রিগো দুতের্তের মেয়ে ও ফিলিপাইনের বর্তমান ভাইস প্রেসিডেন্ট সারা দুতের্তেকে পার্লামেন্টে অভিশংসিত করতে চায় নাগরিক সমাজের একটি জোট', 'সাবেক প্রেসিডেন্ট রড্রিগো দুতের্তের মেয়ে ও ফিলিপাইনের বর্তমান ভাইস প্রেসিডেন্ট সারা দুতের্তেকে পার্লামেন্টে অভিশংসিত করতে চায় নাগরিক সমাজের একটি জোট\nসাবেক প্রেসিডেন্ট রড্রিগো দুতের্তের মেয়ে ও ফিলিপাইনের বর্তমান ভাইস প্রেসিডেন্ট সারা দুতের্তেকে পার্লামেন্টে অভিশংসিত করতে চায় নাগরিক সমাজের একটি জোট। এ জন্য সোমবার তারা অভিযোগ দাখিল করেছে। এতে সারার বিরুদ্ধে অসদাচরণ ও সংবিধান লঙ্ঘনের অভিযোগ আনা হয়েছে। এ খবর দিয়েছে বার্তা সংস্থা রয়টার্স। উল্লেখ্য, বর্তমান প্রেসিডেন্ট ফার্দিনান্দ মার্কোস জুনিয়রের সঙ্গে তীব্র তিক্ত বিরোধিতা চলছে সারা দুতের্তের। তার বিরুদ্ধে পার্লামেন্টের প্রতিনিধি পরিষদ খরচের খাত নিয়ে তদন্ত করছে। তবে কোনো অন্যায় করার কথা প্রত্যাখ্যান করেছেন সারা। খবরে বলা হয়েছে, সোমবার যারা অভিযোগ দিয়েছেন তার মধ্যে আছেন নাগরিক সমাজ ও ধর্মীয় নেতারা। আছেন তার পিতার সমালোচক সাবেক সরকারি কর্মকর্তারাও। অভিযোগকারীদের একজন তেরেসিতা কুইন্টোস ডেলেস বলেন, ভাইস প্রেসিডেন্ট তার সরকারি অফিসকে সহিংস বক্তব্য ছড়ানোর, ব্যক্তিগত সম্পদ অর্জন সহ দায়মুক্তির একটি হাতিয়ার হিসেবে ব্যবহার করছেন। ফিলিপাইনের প্রতিনিধি পরিষদে সারা দুতের্তের বিরুদ্ধে অভিযোগ অনুমোদন করেছেন বিরোধী আকিয়ানের একজন প্রতিনিধি। ২০২২ সালের নির্বাচনে ফার্দিনান্দ মারকোস ভূমিধস জয় পান। সে পর্যন্ত তার সঙ্গে দুতের্তের পরিবারের শক্তিশালী রাজনৈতিক জোট ছিল। কিন্তু মার্কোস বড় জয় পাওয়ার পর তাদের সেই সম্পর্কের বন্ধন ভেঙে যায়। শুরু হয় বিরোধিতা। সারা দুতের্তের পিতা রড্রিগো দুতের্তের মাদক বিরোধী অভিযানের কঠোর সমালোচক ছিলেন লেইলা ডি লিমা। তিনি বলেন, সারার বিরুদ্ধে এই অভিশংসন প্রচেষ্টা শুধু আইনগত লড়াই নয়। একই সঙ্গে জনগণের আত্মমর্যাদা পুনঃপ্রতিষ্ঠার একটি নৈতিক লড়াই। উত্থাপিত অভিযোগে সারা দুতের্তের বিরুদ্ধে ফিলিপাইনের সংবিধান লঙ্ঘনের অভিযোগ আনা হয়েছে। কারণ, তিনি তার বাজেট সম্পর্কে শুনানিতে উপস্থিত হতে রাজি হননি। একই সঙ্গে তার বিরুদ্ধে অযোগ্যতা এবং দায়িত্বহীনতার অভিযোগ আছে। উল্লেখ্য, সম্প্রতি সারা দুতের্তে বলেছেন তিনি প্রেসিডেন্ট মার্কোস, তার স্ত্রী এবং প্রতিনিধি পরিষদের স্পিকার মার্টিন রোমুয়ালদেজকে হত্যা করতে যোগাযোগ করেছেন কারও সঙ্গে। পরে তিনি বলেছেন, তার বক্তব্যকে বিকৃতভাবে উপস্থাপন করা হয়েছে।', '2024-12-03 00:00:00', '2024-12-03 03:47:52', '[\"https://mzamin.com/uploads/news/main/138299_11.webp\"]', 1813, '07f3ae2e9659a7d59a285300432a469b');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746212, 2268, 'https://mzamin.com/news.php?news=138298', 'https://mzamin.com/news.php?news=138298', 'সংবাদ', NULL, 'পাঁচ দফা দাবিতে দিল্লি অভিমুখে ভারতের কৃষকেরা', 'ফের আন্দোলনে উত্তপ্ত হচ্ছে ভারতের রাজধানী নয়াদিল্লি', 'ফের আন্দোলনে উত্তপ্ত হচ্ছে ভারতের রাজধানী নয়াদিল্লি\nফের আন্দোলনে উত্তপ্ত হচ্ছে ভারতের রাজধানী নয়াদিল্লি। পাঁচ দফা দাবি নিয়ে সোমবার সংসদ ভবন অভিযানের ডাক দেয় ভারতীয় কিষাণ পরিষদ (বিকেপি)। নতুন তিন কৃষি আইনের আওতায় কৃষকদের ক্ষতিপূরণ এবং সুযোগ-সুবিধা সংক্রান্ত দাবি-দাওয়া তুলে ধরার লক্ষ্যে এই কর্মসূচিতে রাস্তায় নেমেছেন কৃষকরা। ভারতীয় মিডিয়ায় বলা হয়, সোমবার দুপুর ১২টায় নয়ডার মহামায়া উড়ালপুল থেকে মিছিল শুরু হওয়ার কথা ছিল। হেঁটে এবং ট্রাক্টরে চেপে আন্দোলনকারী কৃষকেরা এগিয়ে যাবেন দিল্লির সংসদ ভবনের উদ্দেশ্যে। রোববার বিকেপি নেতা সুখবীর খলিফা এই কর্মসূচি ঘোষণা করেছে। গৌতম বুদ্ধ নগর, বুলন্দশহর, আলিগড়, আগরা-সহ এই বিক্ষোভ মিছিলে শামিল হন ২০ জেলার কৃষক।মিছিল ঘিরে কোনো অপ্রীতিকর পরিস্থিতি এড়াতে সকাল থেকেই তৎপর ছিল পুলিশ প্রশাসন। নয়ডা-দিল্লি সীমানায় বসানো হয় ব্যারিকেড। চলে পুলিশের চেকিং। দিল্লির পুলিশ কমিশনার (আইনশৃঙ্খলা) শিবহরি মীনা জানিয়েছেন, মহামায়া উড়ালপুল, চিল্লা এবং দিল্লি-নয়ডা সরাসরি সংযোগকারী রাস্তায় প্রচুর পরিমাণে পুলিশকর্মী মোতায়েন করা হয়। নয়ডা-গ্রেটার নয়ডা এক্সপ্রেসওয়েতেও প্রচুর চেক পয়েন্ট বসানো হয়। দিল্লি-নয়ডা সংযোগকারী বেশ কিছু রাস্তায় যান চলাচল নিয়ন্ত্রণ করা হয়। পণ্যবাহী গাড়িগুলোকে অন্য পথ দিয়ে ঘুরিয়ে দেয়া হয়। সংবাদ সংস্থা এএনআই জানিয়েছে, চিল্লা এবং দিল্লি-নয়ডা সরাসরি সংযোগকারী আট লেনের রাস্তায় যানজট তৈরি হয়।সোমবারের মিছিলের মাধ্যমে পাঁচ দফা দাবি সংসদ ভবন পর্যন্ত পৌঁছে দিতে চায় বিকেএস। তার মধ্যে অন্যতম, জমি অধিগ্রহণ সংক্রান্ত ক্ষেত্রে কৃষকদের দাবি। পুরনো অধিগ্রহণ আইন অনুসারে, ১০ শতাংশ জমি এবং ৬৪ দশমিক ৭ শতাংশ বর্ধিত হারে ক্ষতিপূরণ দিতে হবে। ২০১৪ সালের ১লা জানুয়ারির পরে কোনো জমি অধিগ্রহণ হলে, ২০ শতাংশ জমি এবং বাজার দরের চার গুণ বেশি ক্ষতিপূরণ দিতে হবে। ভূমিহীন কৃষকদের সন্তানের জন্য পুনর্বাসন এবং চাকরির ব্যবস্থা করতে হবে।এ ছাড়া উচ্চ ক্ষমতাসম্পন্ন কমিটির (হাই পাওয়া?র্ড কমিটি) সুপারিশগুলো নিয়ে সরকারি নির্দেশিকা জারি করতে হবে। ফসলের ন্যূনতম সহায়ক মূল্য সংক্রান্ত বিষয়ে কৃষক অভিযোগ নিয়ে সুপ্রিম কোর্ট একটি উচ্চ ক্ষমতাসম্পন্ন কমিটি গঠন করে দেয়। সেই কমিটির প্রস্তাবগুলোর বিষয়ে এবার সরকারি নির্দেশিকা চাইছে বিকেপি। পাশাপাশি বসত এলাকার বিষয়েও গ্রহণযোগ্য নিষ্পত্তি চাইছেন আন্দোলনকারী কৃষকেরা।', '2024-12-03 00:00:00', '2024-12-03 03:47:53', '[\"https://mzamin.com/uploads/news/main/138298_2.webp\"]', 1813, '2218f5e930eb8687e774aa9dcb5a9dcd');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746213, 2268, 'https://mzamin.com/news.php?news=138352', 'https://mzamin.com/news.php?news=138352', 'সংবাদ', NULL, 'করোনাকালে বাংলাদেশসহ বিভিন্ন দেশে ব্যাপক দুর্নীতি হয়েছে', 'কোভিড-১৯ বা করোনা মহামারির সময় বাংলাদেশের স্বাস্থ্যখাতে ব্যাপক দুর্নীতি হয়েছে', 'কোভিড-১৯ বা করোনা মহামারির সময় বাংলাদেশের স্বাস্থ্যখাতে ব্যাপক দুর্নীতি হয়েছে\nকোভিড-১৯ বা করোনা মহামারির সময় বাংলাদেশের স্বাস্থ্যখাতে ব্যাপক দুর্নীতি হয়েছে। ব্যক্তিগত সুরক্ষা সরঞ্জাম (পিপিই) কেনা থেকে শুরু করে করোনা পরীক্ষার টেস্টিং কিট ক্রয়ে অস্বচ্ছতা আছে। এক গবেষণায় এমনটা দাবি করেছেন অস্ট্রেলিয়ার মারডক ইউনিভার্সিটির সাসটেইনেবল অ্যাকাউন্টিং অ্যান্ড গভর্ন্যান্সের সহযোগী অধ্যাপক মোয়াজ্জেম হোসেন। তার ও তার দলের গবেষণায় দেখা গেছে, যখন জনস্বাস্থ্য সুরক্ষার জন্য সরকারের তৎপরতা সবচেয়ে বেশি প্রয়োজন ছিল, তখন সরকারি কর্মকর্তাদের দুর্নীতি জনগণের স্বাস্থ্য সুরক্ষার জন্য বড় হুমকি হয়ে দাঁড়ায়। এর জন্য কর্মকর্তা, মন্ত্রী বা কোনো স্বাস্থ্য বিষয়ক কর্মকর্তাকে জবাবদিহিতার মুখোমুখি হতে হয়নি। উল্লেখ্য, শিক্ষকতার ধরন এবং কর্মস্থলে উপযোগী পাঠদান পদ্ধতির জন্য ২০১৬, ২০১৮ ও ২০১৯ সালে মারডক বিজনেস স্কুলের শ্রেষ্ঠ শিক্ষক হয়েছেন মোয়াজ্জেম হোসেন। পেয়েছেন পিভিসি অ্যাওয়ার্ড ফর এক্সিলেন্স ইন টিচিং অ্যান্ড লার্নিং ২০২০। তার গবেষণায় উঠে এসেছে যে, করোনা মহামারির সময় পিপিই এবং অন্যান্য স্বাস্থ্য সামগ্রীর ক্রয় প্রক্রিয়ায় দুর্নীতি এতটাই ব্যাপক ছিল যে, দেশের স্বাস্থ্য ব্যবস্থাপনা কার্যত ভেঙে পড়েছিল। সরকারি কর্মকর্তারা নিয়মিত অভিযোগ অস্বীকার করছিলেন। তারা যে কৌশল অনুসরণ করছিলেন তা একান্তই তাদের ব্যক্তিগত স্বার্থরক্ষার জন্য। গবেষণায় দেখা গেছে, মন্ত্রী ও কর্মকর্তাদের অবহেলার কারণে হাজার হাজার মানুষ সঠিক স্বাস্থ্যসেবা থেকে বঞ্চিত হয়েছেন। ফলে দুর্নীতি আরও বেড়েছে এবং সরকারের প্রতি জনসাধারণের আস্থার সংকট সৃষ্টি হয়েছে।ড. মোয়াজ্জেম হোসেন এবং তার দলের গবেষণার ফলাফল শুধুমাত্র বাংলাদেশের জন্য নয়, বরং অন্যান্য উন্নয়নশীল দেশের ক্ষেত্রেও প্রযোজ্য। দুর্নীতি এবং অস্বীকারের কৌশলের এই ধরনটি বিশ্বব্যাপী একটি স্বীকৃত প্রবণতা হয়ে দাঁড়িয়েছে। উন্নয়নশীল দেশগুলোতে দুর্নীতির বিরুদ্ধে লড়াই করা বিশেষভাবে চ্যালেঞ্জিং। কারণ এখানকার রাজনৈতিক এবং প্রশাসনিক কাঠামো দুর্নীতির বিরুদ্ধে প্রয়োজনীয় ব্যবস্থা নিতে ব্যর্থ হয়। এ ধরনের গবেষণার মাধ্যমে বোঝা যায় যে, দুর্নীতি কীভাবে একটি দেশের অগ্রগতিকে বাধাগ্রস্ত করে এবং সাধারণ মানুষের জীবনকে আরও কঠিন করে তোলে।তাদের গবেষণায় প্রকাশিত হয়েছে যে, কোভিড-১৯ মহামারির সময় বাংলাদেশের মতো বেশ কয়েকটি উন্নয়নশীল দেশে মন্ত্রী এবং উচ্চপদস্থ কর্মকর্তারা ‘প্রত্যাখ্যানের কৌশল’ ব্যবহার করে নিজেদের বিরুদ্ধে উত্থাপিত দুর্নীতির অভিযোগ থেকে বাঁচার চেষ্টা করেছেন। এই গবেষণা অক্সফোর্ড ইউনিভার্সিটি প্রেস এবং লন্ডন স্কুল অফ ট্রপিক্যাল মেডিসিন থেকে প্রকাশিত ‘হেলথ পলিসি অ্যান্ড প্ল্যানিং’ জার্নালে প্রকাশিত হয়েছে এবং এটি বিশ্বব্যাপী নীতিনির্ধারক এবং গবেষকদের মধ্যে ব্যাপক আলোচনার জন্ম দিয়েছে।গবেষণায় উল্লেখ করা হয়েছে যে, যখন মন্ত্রী ও সরকারি কর্মকর্তারা দুর্নীতির অভিযোগের সম্মুখীন হন, তখন তারা তিন ধাপের অস্বীকার বা প্রত্যাখ্যানের কৌশল ব্যবহার করেন। প্রথমত, তারা সরাসরি অভিযোগ অস্বীকার করেন এবং দাবি করেন যে এমন কোনো ঘটনা ঘটেনি। দ্বিতীয়ত, তারা ঘটনার অর্থ পরিবর্তন করে এমনভাবে ব্যাখ্যা করেন যেন সেটি দুর্নীতির অংশ না হয়। তৃতীয়ত, তারা ঘটনাকে বৈধ প্রমাণের চেষ্টা করেন এবং জনগণের মধ্যে এমন একটি ভুল ধারণা তৈরি করেন যে ঘটনাটি ততটা গুরুতর নয়। এই তিন ধাপের মাধ্যমে মন্ত্রী এবং কর্মকর্তারা সফলভাবে দুর্নীতির অভিযোগ থেকে নিজেদের বাঁচিয়ে রাখতে সক্ষম হন।', '2024-12-03 00:00:00', '2024-12-03 03:47:53', '[]', 1813, 'a4634bab93d73018054757f656767cc8');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746214, 2268, 'https://mzamin.com/news.php?news=138297', 'https://mzamin.com/news.php?news=138297', 'সংবাদ', NULL, 'টানা ৭ ম্যাচে জয়হীন সিটি, দর্শকদের দুয়োতে ছয় আঙ্গুল দেখালেন  গার্দিওলা', 'টানা ছয় ম্যাচ জয়ছাড়া থেকে ইংলিশ লীগে লিভারপুলের বিপক্ষে খেলতে নামে ম্যানচেস্টার সিটি', 'টানা ছয় ম্যাচ জয়ছাড়া থেকে ইংলিশ লীগে লিভারপুলের বিপক্ষে খেলতে নামে ম্যানচেস্টার সিটি\nটানা ছয় ম্যাচ জয়ছাড়া থেকে ইংলিশ লীগে লিভারপুলের বিপক্ষে খেলতে নামে ম্যানচেস্টার সিটি। ম্যাচে ২-০ গোলে হারের পর এখন জয়হীন ম্যাচের সংখ্যাটা এক বেড়ে গিয়ে সাত। মাঠ থেকে দর্শকরা দুয়ো দিলে হাতের ছয় আঙ্গুল উঁচিয়ে তার আমলে জেতা শিরোপার সংখ্যাটা মনে করিয়ে দেন কোচ পেপ গার্দিওলা। কয়েকদিন আগে চ্যাম্পিয়ন্স লীগে রিয়াল মাদ্রিদকে হারিয়ে বেশ ফুরফুরে মেজাজে নিজদের মাঠে নামে লিভারপুল। রোববার অ্যানফিল্ডে অলরেডদের হয়ে প্রথম গোল করেন কডি গ্যাকপো। ম্যাচের ১২তম মিনিটে সালাহর নিচু ক্রসকে গোলে পরিণত করেন তিনি। বিরতির আগে আরও বেশ কয়েকটি গোলের সুযোগ হাতছাড়া করে লিভারপুল। তার মধ্যে ডিফেন্ডার ভার্জিল ফন ডাইকের দুইটির মধ্যে একটি হেড ফিরে আসে গোলবারে লেগে। দ্বিতীয়ার্ধেও বেশ কিছু গোলের সুযোগ পায় লিভারপুল। ম্যাচের ৭৮তম মিনিটে সিটির ম্যাচে ফেরার আশা মাটি করে দেন মিশরীয় ফরোয়ার্ড মোহাম্মদ সালাহ। বক্সের ভেতর লুইস দিয়াজকে ফেলে দেন ওর্তেগা। পেনাল্টি থেকে সালাহ গোল করলে ২-০ গোলে এগিয়ে যায় আর্নে স্লটের দল। এটা ছিল সালাহর চলতি মৌসুমে ১১তম গোল। পুরো ম্যাচেই ছন্নছাড়া ছিল সিটির খেলোয়াড়রা। গোলের দিকে আটটি শট নিয়ে পেপ শিষ্যরা লক্ষ্যে রাখতে পারে মাত্র একটি। মূলত সাত ম্যাচের ছয়টিতেই হেরে মাঠে বিব্রতকর অবস্থায় পড়তে হয় ম্যান সিটি কোচ পেপ গার্দিওলাকে। প্রথম গোলে লিভারপুলের এগিয়ে যাওয়ার পরেই দর্শকরা তাকে উদ্দেশ্য করে বলতে থাকে, ‘কাল সকালেই চাকরি চলে যাবে তোমার।’ যদিও গার্দিওলাও কম যান না। তিনি দর্শকদের উদ্দেশ্যে হাতের ছয়টি আঙ্গুল দেখিয়ে দেন। যা দ্বারা তিনি তার আমলে জেতা ছয়টি লীগ শিরোপাকে ইঙ্গিত করেন। ম্যাচের পর দর্শকদের এমন আচরণে পেপ বলেন, ‘আমি লিভারপুল দর্শকদের থেকে এমনটা আশা করিনি। তবে ঠিকই আছে, এটাও খেলার একটা অংশ। আমি ব্যাপারটা বুঝি। ওদের সঙ্গে আমরা অনেক অবিশ্বাস্য ম্যাচ খেলেছি। তাদের প্রতি আমার অনেক শ্রদ্ধা আছে।’ তিনি আরও বলেন, ‘আমাকে স্টেডিয়ামের সবাই এখন বরখাস্ত করতে চায়। এমনটা শুরু হয়েছে ব্রাইটনে। তারা হয়তো ঠিক। কারণ ম্যাচের ফল বাজে আসছে।’ সিটির এমন নড়বড়ে অবস্থার মধ্যেই সম্প্রতি চুক্তি নবায়ন করেন পেপ গার্দিওলা। তবে তিনি আগেই এটা নিশ্চিত করেছেন যে ফল ভালো না দেখলে তিনি বেশিদিন এখানে থাকবেন না। লিভারপুলের বিপক্ষে এই পরাজয়ে তালিকার পাঁচে নেমে যাওয়া ম্যান সিটির সংগ্রহ ২৩ পয়েন্ট। ১৩ ম্যাচে ১১ জয় ও এক ড্র নিয়ে শীর্ষে অবস্থান লিভারপুলের। ২৫ পয়েন্ট নিয়ে দুইয়ে আছে আর্সেনাল।', '2024-12-03 00:00:00', '2024-12-03 03:47:54', '[\"https://mzamin.com/uploads/news/main/138297_sp.webp\"]', 1813, '0a976983b6613cb794636048f1e2b3c4');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746215, 2268, 'https://mzamin.com/news.php?news=138351', 'https://mzamin.com/news.php?news=138351', 'সংবাদ', NULL, 'ছেলে হান্টারকে নিঃশর্ত ক্ষমা করলেন বাইডেন', 'নিজ ছেলে হান্টারকে আনুষ্ঠানিকভাবে ক্ষমা করলেন মার্কিন প্রেসিডেন্ট জো বাইডেন', 'নিজ ছেলে হান্টারকে আনুষ্ঠানিকভাবে ক্ষমা করলেন মার্কিন প্রেসিডেন্ট জো বাইডেন\nনিজ ছেলে হান্টারকে আনুষ্ঠানিকভাবে ক্ষমা করলেন মার্কিন প্রেসিডেন্ট জো বাইডেন। দুইটি ফৌজদারি মামলার জন্য শাস্তির মুখোমুখি হয়েছিলেন হান্টার। এক বিবৃতিতে মার্কিন প্রেসিডেন্ট বলেছেন, তার ছেলেকে অন্যায়ভাবে লক্ষ্যবস্তু করা হয়েছে এবং ছেলের বিরুদ্ধে মামলার রায় ‘বিচারিক প্রহসন’ বলেও আখ্যা দিয়েছেন তিনি। এ খবর দিয়েছে অনলাইন বিবিসি।এতে বলা হয়, কর ফাঁকি দেয়ার অভিযোগে সেপ্টেম্বরের শুরুতে অভিযুক্ত হন হান্টার বাইডেন। জুনে মাদক সেবন ও অস্ত্রের বিষয়ে মিথ্যা তথ্য দেয়ার অভিযোগেও অভিযুক্ত হয়েছিলেন তিনি। তবে তিনি এখনো শাস্তির মুখোমুখি হন নি।ছেলেকে নিঃশর্তভাবে ক্ষমা করেছেন জো বাইডেন। যদিও এর আগে বাইডেন জানিয়েছিলেন যে, ছেলেকে তিনি ক্ষমা করবেন না। মাত্র দুই মাস আগে হোয়াইট হাউসের পক্ষ থেকেও জানানো হয়েছিল, বাইডেন তার ছেলেকে ক্ষমা করছেন না। কিন্তু মার্কিন প্রেসিডেন্ট নির্বাচনের পর নিজের কথার ওপর অনড় থাকতে পারলেন না বাইডেন। এর আগে বিচার বিভাগের সিদ্ধান্তের ওপর হস্তক্ষেপ না করার কথা জানিয়েছিলেন তিনি। তবে এসব মামলা রাজনৈতিক উদ্দেশ্যপ্রণোদিত বলেও মনে করেন বাইডেন। তিনি বলেছেন, শুধু আমার ছেলে বলেই তাকে লক্ষ্যবস্তু করা হয়েছে এবং অযথা মামলায় জড়ানো হয়েছে। তবে ছেলের বিষয়ে সিদ্ধান্ত নিতে সংগ্রাম করতে হয়েছে বলে জানিয়েছেন মার্কিন প্রেসিডেন্ট। তিনি বলেছেন, একবার এই সিদ্ধান্ত নেয়ার পর তা বিলম্ব করার কোনো কারণ নেই। আশা করি আমেরিকানরা বুঝবেন কেন প্রেসিডেন্ট হয়েও একজন পিতা এমন সিদ্ধান্ত নেন।তবে বাইডেনের এই ক্ষমা দেয়ার বিষয়টি ভালোভাবে নেন নি প্রেসিডেন্ট নির্বাচিত ডনাল্ড ট্রাম্প। তিনি বলেছেন, ছেলে হান্টারকে জো বাইডেন যে ক্ষমা করলেন তাতে কি (৬ই জানুয়ারি) ওই জিম্মিদেরও অন্তর্ভুক্ত করা হয়েছে, যারা বছরের পর বছর কারাগারে রয়েছেন? এটাই আসল দুর্ব্যবহার এবং অবিচার। মূলত ট্রাম্প তার সমর্থকদের প্রতি ইঙ্গিত করেছেন, যারা ২০২১ সালের ৬ই জানুয়ারি ক্যাপিটলে হামলার জন্য দায়ী। ২০২০ সালে নির্বাচনের ফলাফল উল্টানোর লক্ষ্যে এমন কাজ করেছিলেন বলে ট্রাম্প এবং তার সমর্থকদের বিরুদ্ধে অভিযোগ রয়েছে।', '2024-12-03 00:00:00', '2024-12-03 03:47:54', '[]', 1813, '30c42083771386c6e18040a798219855');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746216, 2268, 'https://mzamin.com/news.php?news=138382', 'https://mzamin.com/news.php?news=138382', 'সংবাদ', NULL, 'রানার তোপে ১৮ রানের লিড বাংলাদেশের', 'প্রথম ইনিংসে মাত্র ১৬৪ রানের পুঁজি', 'প্রথম ইনিংসে মাত্র ১৬৪ রানের পুঁজি। এমন মামুলি সংগ্রহ নিয়েই ১৮ রানের লিড বাংলাদেশের। সফরকারী দলকে এই লিড এনেদিয়েছেন নাহিদ রানা। পাঁচ উইকেট নিয়েছেন তরুন এই পেসার। ক‍্যারিয়ারে ষষ্ঠ টেস্টে প্রথম পাঁচ উইকেট রানার। দুটি নিয়েছেন হাসান মাহমুদ। একটি করে উইকেট নিয়েছেন তাসকিন, তাইজুল ও মেহেদি হাসান মিরাজ।\nতৃতীয় দিনের শুরু থেকেই স্বাগতিক ওয়েস্ট ইন্ডিজকে চেপে ধরে বাংলাদেশের বোলাররা। আলাদা করে বলতে হয় নাহিদ রানার কথা। তরুণ এই পেসারের নিয়মিত ১৫০-এর আশপাশে করা বলগুলো খেলতে গলদঘর্ম হয়েছেন ক্যারিবীয় ব্যাটাররা।\nদিনের শুরুতেই নাহিদ রানার বলে পরপর দুই উইকেট হারায় ওয়েস্ট ইন্ডিজ। এরপর তাসকিন এবং তাইজুলের বলেও দ্রুত আরও দুই উইকেট হারিয়েছে ক্যারিবীয়রা। এরপর উইকেট শিকারে যোগ দেন হাসান মাহমুদ। তিনিও নেন দুই উইকেট। সব মিলিয়ে স্বাগতিকদের ১৪৬ রানে অলআউট করে দেয় বাংলাদেশের বোলাররা।\nনাহিদ রানার আগুনে বোলিংয়ের সামনে কুপোকাত হন ওয়েস্ট ইন্ডিজ অধিনায়ক ব্র্যাথওয়েট। ১৪২ কিলোমিটার গতিতে ছুটে আসা বলে নিয়ন্ত্রণ রাখতে পারেননি ক্যারিবীয় দলপতি। ব্যাক ফুটে গিয়ে বলটা বলটা ব্লক করার চেষ্টা করেন; কিন্তু ব্যাটের উপরের অংশে লাগিয়ে ক্যাচ তুলে দেন গালি অঞ্চলে। সেখানে নিচু হয়ে আসা ক্যাচটি তালুবন্দী করে নেন বদলি ফিল্ডার জাকির হাসান।\n৩৯ রান করে ফিরে যান ব্র্যাথওয়েট। ৩৩ রান নিয়ে তৃতীয় দিনের খেলা শুরু করেছিলেন তিনি। আজ মাত্র ৬ রান যোগ করতে পারলেন। দলীয় ৮৫ রানের মাথায় আউট হয়ে যান ক্যারিবীয় অধিনায়ক।\nএরপর ব্যাট করতে নামা কাভেম হজকে সহজ ক্যাচে পরিণত করেন নাহিদ রানা। ৪৫তম ওভারের চতুর্থ বলে পুল রতে গিয়েই ব্যাটের উপরের অংশে লাগিয়ে আকাশে বল তুলে দেন হজ। ফাইন লেগে ক্যাচ ধরতে ছুটে যান উইকেটরক্ষক লিটন। কিন্তু তার আগেই সেখানে পৌঁছান তাসকিন। কিন্তু বল বেশি ওপরে থাকায় সহজ ক্যাচটা মিস করে ফেলেন তাসকিন। তার হাত ফসকে বল পড়ে যায় মাটিতে।\nএক বল বিরতি দিয়েই হজের উইকেট তুলে নেন নাহিদ রানা। এবার নাহিদ রানার চেয়ে এই উইকেটের কৃতিত্ব সবচেয়ে বেশি উইকেটরক্ষক লিটন দাসের। বাজপাখির মত ঝাঁপিয়ে পড়ে যেভাবে কাভেম হজের ব্যাটের কানায় লেগে ছুটে আসা বলটিকে এক হাতে তালুবন্দী করলেন, তা রীতিমত অবিশ্বাস্য। ৩ রান করে আউট হয়ে যান কাভেম হজ।\nঅ্যালিক আথানাজেকে বেশিক্ষণ টিকতে দেননি তাসকিন আহমেদ। তার বলে বোল্ড হয়েই মাত্র ২ রান করে সাজঘরে ফেরেন এই ক্যারিবীয় মিডল অর্ডার। জাস্টিন গ্রিভসকেও ঘূর্ণিতে বোকা বানান তাইজুল ইসলাম। সোজা বোল্ড করে সাজঘরে পাঠান। ২ রান করে আউট হন গ্রিভস।\nএরপর ৫ রান করা জসুয়া সিলভাকে এলবিডব্লিউর ফাঁদে ফেলেন হাসান মাহমুদ। নিজের পরের ওভারে সেট ব্যাটার কিয়েসি কার্টিকেও (৪০) তুলে নেন এই পেসার। লাঞ্চের আগে আলজেরি জোসেফকে আউট করে নিজের চতুর্থ উইকেট শিকার করেন নাহিদ রানা। ১২৩ রানে ৮ উইকেট হারায় ক্যারিবীয়রা।\nএর আগে দ্বিতীয় দিন ১৬৪ রানে অলআউট হয় বাংলাদেশ। জবাবে ব্যাট করতে নেমে দ্বিতীয় দিন শেষ বিকেলে ২৫ রানের মাথায় প্রথম উইকেট হারায় ক্যারিবীয়রা। মিকাইল লুইস ১২ রান করে আউট হন নাহিদ রানার বলে।', '2024-12-03 00:00:00', '2024-12-03 03:47:56', '[\"https://mzamin.com/uploads/news/main/138382_IMG_4032.jpeg\"]', 1813, 'b7824bd476ff3c1cc7d524546d990341');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746217, 2268, 'https://mzamin.com/news.php?news=138381', 'https://mzamin.com/news.php?news=138381', 'সংবাদ', NULL, 'মালয়েশিয়া থেকে দেশে ফিরলেন আলোচিত সাবেক কূটনীতিক খাইরুজ্জামান', '১৫ বছরের নির্বাসন শেষে অবশেষে মুক্ত হলেন মালয়েশিয়ায় আশ্রয় নেয়া বাংলাদেশের সাবেক কূটনীতিক মোহাম্মদ খাইরুজ্জামান', '১৫ বছরের নির্বাসন শেষে অবশেষে মুক্ত হলেন মালয়েশিয়ায় আশ্রয় নেয়া বাংলাদেশের সাবেক কূটনীতিক মোহাম্মদ খাইরুজ্জামান।\nমালয়েশিয়ার সাবেক হাইকমিশনার খাইরুজ্জামান জানান, আগস্টে গণবিক্ষোভের মাধ্যমে শেখ হাসিনা সরকার পতনের পর তিনি দেশে ফিরে যাওয়ার সাহস পেয়েছেন।\nসে সময়ের স্বৈরাচারী সরকার আমাকে মিথ্যা অভিযোগে টার্গেট করেছিল। কিন্তু এখন আমরা মুক্ত। আমার পরিবারের জন্য এটি একটি স্বপ্ন পূরণের মতো যে, আমরা আবার নির্ভয়ে স্বাভাবিক বাংলাদেশি নাগরিকের মতো জীবন যাপন করতে পারব,” বললেন তিনি।\n২৭ নভেম্বর কুয়ালালামপুর থেকে ঢাকা ফিরে যান খাইরুজ্জামান। তার সঙ্গে ছিলেন স্ত্রী রিটা রহমান এবং ছেলে আয়হিম আর জেড জামান। রিটা ও আয়হিম ২০১৯ সালে নিরাপত্তার কারণে যুক্তরাষ্ট্রে চলে যান। আরেক ছেলে ফিনল্যান্ডে চলে যান। এতদিন তাদের কারোরই খাইরুজ্জামানের সঙ্গে দেখা করার সুযোগ হয়নি।\nঢাকা ফেরার আগে ফ্রি মালয়েশিয়া টুডেকে (এফএমটি) দেয়া সাক্ষাৎকারে ৭৩ বছর বয়সী এই সাবেক কূটনীতিক মালয়েশিয়া সরকার এবং জাতিসংঘের শরণার্থী সংস্থার (ইউএনএইচসিআর) প্রতি কৃতজ্ঞতা প্রকাশ করেন।\n২০০৭ থেকে ২০০৯ সাল পর্যন্ত আমি মালয়েশিয়ায় বাংলাদেশের হাইকমিশনার হিসেবে দায়িত্ব পালন করি। শেখ হাসিনা সরকার ক্ষমতায় আসার পর আমাকে ঢাকায় তলব করা হয়। কিন্তু আমি জানতাম, দেশে ফিরলে গুম হয়ে যাব। আদালত আমার বিরুদ্ধে সব মামলা খারিজ করলেও তারা ভিত্তিহীন অভিযোগ তুলেছিল, বললেন তিনি।\nতিনি ফ্রি মালয়েশিয়া টুডেকে আরও বলেন, “আমি তখন ইউএনএইচসিআরকে জানাই, ঢাকায় ফিরলে আমার জীবন হুমকির মুখে পড়বে। এরপর আমি আনুষ্ঠানিকভাবে ইউএনএইচসিআরের কার্ডধারী শরণার্থী হয়ে যাই।”\n২০২২ সালের ফেব্রুয়ারিতে খাইরুজ্জামানকে দেশে ফেরানোর চেষ্টা করে বাংলাদেশ সরকার। মালয়েশিয়ার ইমিগ্রেশন বিভাগ তাকে গ্রেপ্তার করলেও তার আইনজীবীরা হেবিয়াস কর্পাসের মাধ্যমে আদালতে মামলা করেন এবং রায় পক্ষে যায়। এবং ইউএনএইচসিআর এবং মালয়েশিয়ার মানবাধিকার কমিশনের (সুহাকাম) চাপেই তাকে মুক্তি দেয়া হয়।\nখাইরুজ্জামান বলেন, আটকের প্রথম দুই দিন আমাকে প্রায় ২০ জন অবৈধ অভিবাসীর সঙ্গে ছোট একটি রুমে থাকতে হয়। ঠান্ডা রুমে কম্বল ছাড়াই রাত কাটানো খুবই কষ্টকর ছিল। পরে তারা জানতে পারে আমি একজন প্রাক্তন রাষ্ট্রদূত। এরপর আমাকে আলাদা রুম ও কম্বল দেয়া হয়। যদিও অভিজ্ঞতাটি অপমানজনক ছিল, তবে আমি সংশ্লিষ্ট কর্মকর্তাদের ক্ষমা করে দিয়েছি কারণ তারা আমার বিষয়ে সঠিক তথ্য জানতেন না।\nবাংলাদেশ সরকার তখন দাবি করে যে, তিনি কুয়ালালামপুরে দায়িত্ব পালনকালে বিপুল পরিমাণ সম্পদ অর্জন করেছিলেন।\n“১৪ বছর পর এই ধরনের অভিযোগ আনা হয়, যা কেবল মালয়েশিয়ার সরকারকে প্রতারণার চেষ্টা ছাড়া কিছুই নয়। তবে মালয়েশিয়া তাদের মিথ্যা অভিযোগ আমলে নেয়নি,” তিনি যোগ করেন।\nএদিকে, খাইরুজ্জামানের স্ত্রী রিটা রহমান জানান, তাদের বড় ছেলে রেহমানুজ্জামান, যিনি ঢাকায় আইটি ব্যবসার সঙ্গে যুক্ত ছিলেন, তাকেও হয়রানি ও নির্যাতনের শিকার হতে হয়। পরে তিনি ফিনল্যান্ডে পাড়ি জমান।\nতিনি বলেন, এই ১৫ বছর আমাদের পরিবারের জন্য ছিল অত্যন্ত যন্ত্রণাদায়ক। বিশেষত যখন জানা যায় বাংলাদেশে রাজনৈতিক প্রতিপক্ষকে গুম করা কোনো আশ্চর্যের বিষয় নয়।\nতবে রিটা বলেন, এত অনিশ্চয়তার মধ্যে থেকে দেশে ফিরতে পারা আমাদের জন্য সবচেয়ে বড় বিষয়। নতুন পরিবেশে মানিয়ে নেয়া কঠিন হবে জানি, কিন্তু আমরা আমাদের পরিবারের জন্য একটি নতুন শুরু করার প্রস্তুতি নিচ্ছি।\nরিটা আরও বলেন, আমার পরিবার যে মানসিক আঘাত পেয়েছে তা কখনোই ভুলে যাওয়া সম্ভব নয়। তবে সঠিক কাজ করার জন্য লড়াই চালিয়ে যেতে হবে।', '2024-12-03 00:00:00', '2024-12-03 03:47:57', '[\"https://mzamin.com/uploads/news/main/138381_IMG-20241203-WA0000.jpg\"]', 1813, 'a8327cdf6817aee82ceaba52b6832594');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746218, 2268, 'https://mzamin.com/news.php?news=138379', 'https://mzamin.com/news.php?news=138379', 'সংবাদ', NULL, 'আগরতলায় বাংলাদেশ হাইকমিশনে হামলা', 'ভারতের আগরতলায় বাংলাদেশ সহকারী হাইকমিশনে হামলা ও ভাঙচুরের ঘটনা ঘটেছে', 'ভারতের আগরতলায় বাংলাদেশ সহকারী হাইকমিশনে হামলা ও ভাঙচুরের ঘটনা ঘটেছে। হামলাকারীরা বাংলাদেশের পতাকা ছিঁড়ে অগ্নিসংযোগ করেছে। গতকাল দুপুরে এই হামলার ঘটনা ঘটে। এ ছাড়া বাংলাদেশের সীমান্ত লাগোয়া আরও কয়েকটি স্থানে ভারতীয়রা বিক্ষোভ করেছেন। তারা বাংলাদেশবিরোধী বিভিন্ন স্লোগান দেন। বিক্ষোভ হয়েছে মুম্বই উপ-দূতাবাসের সামনেও। হামলার ঘটনায় তীব্র প্রতিবাদ জানিয়েছে বাংলাদেশ। পাশাপাশি এ ঘটনায় দুঃখ প্রকাশ করেছে ভারতের পররাষ্ট্র মন্ত্রণালয়।\nবাংলাদেশ সম্মিলিত সনাতনী জাগরণ জোটের মুখপাত্র চিন্ময় কৃষ্ণ দাশকে গ্রেপ্তারের প্রতিবাদে আগরতলায় বাংলাদেশ সহকারী হাইকমিশনের সামনে বিক্ষোভ এবং ভাঙচুরের ঘটনা ঘটে। পরে ঘটনার ভিডিও সামাজিক যোগাযোগ মাধ্যমে ছড়িয়ে পড়ে। এতে দেখা যায়, বিক্ষোভকারীরা সেখানে হামলা করছেন, বাংলাদেশের জাতীয় পতাকা স্ট্যান্ড থেকে নামিয়ে পুড়িয়ে দিচ্ছেন। স্থানীয় সূত্র জানায়, হিন্দু সংঘর্ষ সমিতি নামের একটি সংগঠনের সভা ছিল আগরতলার বাংলাদেশের সহকারী হাইকমিশনের সামনে। সভা শেষে সংগঠনের একটি প্রতিনিধিদল হাইকমিশন কার্যালয়ের ভেতরে যায় স্মারকলিপি জমা দিতে। এসময় বাইরে থাকা কিছু হিন্দু যুবক হঠাৎ বাংলাদেশ সহকারী হাইকমিশন কার্যালয়ের ভেতরে ঢুকে বাংলাদেশের জাতীয় পতাকা নামিয়ে ছিঁড়ে ফেলে এবং আগুন দেন। পরে হাইকমিশন কার্যালয়ের কিছু সাইনবোর্ড ভাঙচুর করে তারা। ঘটনার সময় আইনশৃঙ্খলা রক্ষাকারী বাহিনীর সদস্যরা সেখানে থাকলেও হামলা-ভাঙচুর ঠেকাতে পারেনি।বিক্ষোভকারীদের হাতে ছিল গেরুয়া পতাকা, ইটপাটকেল ও লাঠিসোটা। তাদের জয়শ্রীরাম স্লোগান দিতে শোনা যায়।\nঘটনার পর ভারতের পররাষ্ট্র মন্ত্রণালয় দুঃখ প্রকাশ করেছে। তাদের এক্স হ্যান্ডেলে লেখা হয়, কোনো অবস্থাতেই কূটনৈতিক সম্পত্তিকে লক্ষ্যবস্তুতে পরিণত করা উচিত নয়।এদিকে, ভারতের কোচবিহারেও বিক্ষোভ হয়েছে। সেখানে বাংলাদেশের অন্তর্বর্তীকালীন সরকারের প্রধান উপদেষ্টা অধ্যাপক মুহাম্মদ ইউনূসের কুশপুত্তলিকা পুড়িয়ে বিক্ষোভ দেখায় সনাতনী হিন্দু মঞ্চ। সংগঠনটির সদস্যরা পার্শ্ববর্তী বাংলাদেশ সীমান্ত লাগোয়া চ্যাংড়াবান্ধায় বিক্ষোভ মিছিলও করে। বিক্ষোভ হয়েছে ভারতের পেট্রাপোল সীমান্তেও। সেখানে সমাবেশ হয়েছে পশ্চিমবঙ্গের বিরোধী দলের নেতা শুভেন্দু অধিকারীর নেতৃত্বে।\nমুম্বইয়ে উপ-হাইকমিশনের সামনে বিক্ষোভ: ভারতের মহারাষ্ট্রের রাজধানী মুম্বইয়ে অবস্থিত বাংলাদেশ উপ-হাইকমিশনের কাছে সোমবার বিকালে বিশ্ব হিন্দু পরিষদের কয়েক শ’ বিক্ষোভকারী বিক্ষোভ করেছেন। এ সময় বিক্ষোভকারীরা ব্যানার ও প্ল্যাকার্ড নিয়ে বাংলাদেশ মিশনের প্রায় এক থেকে দেড় শ’ গজের মধ্যে অবস্থান নেন। তারা বাংলাদেশে সংখ্যালঘু নির্যাতন এবং ইসকন সদস্যদের বিরুদ্ধে ব্যবস্থা গ্রহণের প্রতিবাদ জানিয়ে নানা স্লোগান দেন। মুম্বইয়ে বাংলাদেশ উপ-হাইকমিশনের পক্ষ থেকে এই তথ্য নিশ্চিত করা হয়। বিশ্ব হিন্দু পরিষদের আয়োজনে বিক্ষোভে যোগ দিয়েছিলেন মহারাষ্ট্রের রাজ্য সরকারের সাবেক মন্ত্রী ও বিশিষ্ট শিল্পপতি লোধা মঙ্গল প্রভাত।\nফেনী সীমান্তে বিক্ষোভ: ওদিকে ফেনীর সীমান্তবর্তী পরশুরামের বিলোনিয়া স্থলবন্দরের ভারত অংশে বাংলাদেশবিরোধী বিক্ষোভ হয়েছে। সনাতনী হিন্দু সমাজের ব্যানারে কিছু ভারতীয় নাগরিক রোববার বিকালে বিক্ষোভ করেন। সোমবার এ বিষয়ে ভারতীয় সীমান্তরক্ষী বাহিনীর (বিএসএফ) কাছে প্রতিবাদ জানিয়েছে বর্ডার গার্ড বাংলাদেশ (বিজিবি)। বিজিবি-৪ ফেনী ব্যাটালিয়নের অধিনায়ক লে. কর্নেল মোহাম্মদ মোশারফ হোসেন বিষয়টি নিশ্চিত করেছেন।রোববার রাত ১২টার দিকে বিলোনিয়া সীমান্তেও ওই বিক্ষোভের ঘটনার একটি ভিডিও সামাজিক যোগাযোগমাধ্যমে ছড়িয়ে পড়ে।\nপ্রত্যক্ষদর্শী ও স্থানীয় বাসিন্দারা জানান, রোববার বিকাল থেকে সন্ধ্যা পর্যন্ত বিলোনিয়া স্থলবন্দরের ওপারে ৫০ থেকে ৬০ জন ভারতীয় নাগরিক জড়ো হয়ে বাংলাদেশের বিরুদ্ধে বিক্ষোভ করেন। তারা ইসকনের পক্ষে এবং বাংলাদেশ সরকারের বিরুদ্ধে নানা স্লোগান দেন। এ ছাড়া বাংলাদেশে ভারতীয় পণ্য ও বিদ্যুৎ রপ্তানি বন্ধের দাবিতে স্লোগান দেন। এ সময় ভারতের নো-ম্যানস ল্যান্ড অংশে ঢুকে বাংলাদেশের দিকে মাইক তাক করে উচ্চ শব্দে প্রায় আধা ঘণ্টা নানা স্লোগান দেন। পরে বিএসএফ সদস্যরা তাদের সরিয়ে দেন।\nবিজিবি’র মজুমদারহাট বিওপির কোম্পানি কমান্ডার সৈয়দ কামরুল আলম মজুমদার বলেন, ঘটনার পর বিলোনিয়া স্থলবন্দরে বিজিবি’র সদস্যরা অবস্থান নেন। বিএসএফের কোম্পানি কমান্ডারকে ডেকে এ ঘটনার প্রতিবাদ জানানো হয়েছে।', '2024-12-03 00:00:00', '2024-12-03 03:47:58', '[\"https://mzamin.com/uploads/news/main/138379_11.webp\"]', 1813, '426a3c0a2fe1d08882988abc38fc4962');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746219, 2268, 'https://mzamin.com/news.php?news=138378', 'https://mzamin.com/news.php?news=138378', 'সংবাদ', NULL, 'সংবিধান ধর্মনিরপেক্ষ হবে কিনা জানতে চাইলেন এক কূটনীতিক', 'ধর্মীয় সংখ্যালঘু ইস্যুতে সৃষ্ট ঘটনাবলির বিষয়ে সরকারের অবস্থান জানাতে অনুষ্ঠিত পররাষ্ট্র উপদেষ্টার ব্রিফিংয়ে সংস্কার হওয়া সংবিধান কেমন হবে? তা জানতে চাইলেন এক কূটনীতিক', 'ধর্মীয় সংখ্যালঘু ইস্যুতে সৃষ্ট ঘটনাবলির বিষয়ে সরকারের অবস্থান জানাতে অনুষ্ঠিত পররাষ্ট্র উপদেষ্টার ব্রিফিংয়ে সংস্কার হওয়া সংবিধান কেমন হবে? তা জানতে চাইলেন এক কূটনীতিক। বিশেষ করে বিদ্যমান সংবিধান বাতিল করে পুনঃলিখন হচ্ছে কিনা? এবং নতুন সংবিধান ধর্মের প্রশ্নে নিরপেক্ষ হবে কিনা? তা জানার আগ্রহ ছিল পূর্ব এশিয়ার ওই রাষ্ট্রদূতের। রমনার রাষ্ট্রীয় অতিথি ভবন পদ্মায় ছিল সেই জনাকীর্ণ ব্রিফিং। যেখানে সুইডেন এবং সংযুক্ত আবর আমিরাত ছাড়া ঢাকাস্থ প্রায় সব কূটনৈতিক মিশনের জ্যেষ্ঠ কূটনীতিকরা উপস্থিত ছিলেন। উপদেষ্টা সেই প্রশ্নের তাৎক্ষণিক জবাব দেননি, সময় নিয়েছেন। দূতদের তিনি জানিয়েছেন, বিদ্যমান সংবিধান এখনো বহাল। প্রয়োজনীয় সংস্কার আনতে সংবিধান বিষয়ে পড়াশোনা এবং অভিজ্ঞতা রয়েছে এমন প্রতিনিধিদের নিয়ে একটি স্বতন্ত্র কমিশন গঠন করে দিয়েছে সরকার। সেই কমিশন কাজ করছে, ডিসেম্বরের সমাপনীতে সুপারিশসহ তারা তাদের রিপোর্ট জমা দেবেন। সেই রিপোর্ট নিয়ে সরকার এবং স্টেক হোল্ডারদের মধ্যে বিস্তৃত আলোচনার পর চূড়ান্ত সিদ্ধান্ত আসবে। তারপরই বলা যাবে সংবিধান কেমন হবে? ওই ব্রিফিংয়ে পশ্চিমা এক কূটনীতিক জানতে চান ইসকনসহ ধর্মীয় ইস্যুতে সৃষ্ট উত্তেজক পরিস্থিতিতে সরকার হিন্দুসহ অন্যান্য ধর্মীয় সংখ্যালঘুদের সঙ্গে কতটা যোগাযোগ রাখছে অর্থাৎ সংলাপ চলছে কিনা? উপদেষ্টা এ নিয়ে সরকারের সমুদয় উদ্যোগের বিস্তারিত তুলে ধরেন। ব্রিফিংয়ে অস্ট্রেলিয়া মহাদেশের একজন জ্যেষ্ঠ প্রতিনিধির সঙ্গে কথা বলে জানা গেছে। উল্লেখ্য, ব্রিফিং শেষে উপস্থিত গণমাধ্যম প্রতিনিধিদের সঙ্গে কথা বলেন পররাষ্ট্র উপদেষ্টা মো. তৌহিদ হোসেন। সঙ্গে ছিলেন ভারপ্রাপ্ত পররাষ্ট্র সচিব এম রিয়াজ হামিদুল্লাহ। ব্রিফিংয়ে কারা কারা ছিলেন বিশেষ করে ভারতীয় হাইকমিশনের প্রতিনিধিত্ব বিষয়ে জানতে চাইলে উপদেষ্টা বলেন, ভারতসহ প্রায় সব দেশের প্রতিনিধিই ছিলেন। এক’দুটি মিশনের প্রতিনিধি বাস্তব কারণে না-ও আসতে পারেন। এটা স্ক্রুটিনি করলে পুরো পিকচার পাওয়া যাবে। তবে উল্লেখযোগ্য সব মিশনের প্রতিনিধিই ছিলেন। তার বক্তব্যে কূটনীতিকদের প্রতিক্রিয়া কেমন ছিল? জানতে চাইলে উপদেষ্টা বলেন, আমার মনে হয়েছে তারা আমাদের বক্তব্যে কনভিন্স।\nইসকন এবং চিন্ময় দাশ প্রসঙ্গ: বিফ্রিংয়ে ইসকন পুরোহিত চিন্ময় কৃষ্ণ দাশের বিরুদ্ধে উত্থাপিত সুনির্দিষ্ট অভিযোগ বিষয়ে আন্তর্জাতিক সমপ্রদায়কে জানানো হবে বলে দাবি করেন পররাষ্ট্র উপদেষ্টা। সেই সঙ্গে ইসকনসহ সংখ্যালঘু ইস্যুতে ভারতীয় সংবাদমাধ্যমে নানা রকম খবর ছাপা হচ্ছে জানিয়ে তিনি বলেন, এর বেশির ভাগই অসত্য। সাংবাদিকদের সঙ্গে আলাপে উপদেষ্টা বলেন, সব ধর্ম-বিশ্বাসের লোকজনের নিরাপত্তায় সরকার যেসব ইতিবাচক পদক্ষেপ নিয়েছে তা বিশ্ব সমপ্রদায়ের প্রতিনিধিদের সামনে তুলে ধরা হয়েছে। ৫ই আগস্টের পর বাংলাদেশে সংখ্যালঘুদের টার্গেট করে হামলা করা হচ্ছে মর্মে ভারতীয় গণমাধ্যম অরকেস্ট্রেইটেড ওয়েতে যে প্রচার-প্রচারণা চালাচ্ছে তার কোনো ভিত্তি নেই বলে ফের দাবি করেন তিনি। তবে উপদেষ্টা বলেন- ঘটনা যে নাই বা সমস্যা নাই তা আমরা বলছি না। বরং যেসব ঘটনা ঘটেছে তা কো-ইনসিডেন্ট, ব্যক্তিস্বার্থ, পুরনো দ্বন্দ্ব-সংঘাত বা রাজনৈতিক প্রতিযোগিতা বা প্রতিহিংসা থেকে। কূটনৈতিক ব্রিফিংটি পুরোপুরি ফ্লেক্সিবল ছিল জানিয়ে উপদেষ্টা বলেন, এতে কেবল রাষ্ট্রদূত, হাইকমিশনার, চার্জ দ্য অ্যাফেয়ার্স বা মিশনপ্রধানই নন, যেকোনো ডিপ্লোম্যাট ব্রিফিংয়ে অংশ নিয়েছেন। তারা খোলামেলা বিভিন্ন বিষয়ে জানতে চেয়েছেন। চা-খেতে খেতে অনেকে নিজের অভিজ্ঞতা শেয়ার করেছেন। একজন হিন্দু সম্প্রদায়ের একটি বিয়ের অনুষ্ঠানে নির্বিঘ্নে অংশ নেয়ার অভিজ্ঞতা শেয়ার করেছেন জানিয়ে উপদেষ্টা বলেন, আমি তাকে সেই গল্পটা তার অন্য সহকর্মী এবং হেডকোয়ার্টারের রিপোর্টে লিখতে অনুরোধ করেছি।\nমমতাকে চিনি, তার বাড়িতে যাতায়াত ছিল আমার, তিনি তার স্টাইলেই বক্তব্যটি রেখেছেন- পররাষ্ট্র উপদেষ্টা\nএদিকে বাংলাদেশে শান্তিরক্ষী মোতায়েনের আহ্বান সংবলিত মমতা বন্দ্যোপাধ্যায়ের বক্তব্যকে ‘তার ধরনের একটি বক্তব্য’- হিসেবে দেখতে চান পররাষ্ট্র উপদেষ্টা মো. তৌহিদ হোসেন। বাংলাদেশে জাতিসংঘের শান্তিসেনা পাঠানোর আরজি জানাতে ভারতের কেন্দ্রীয় সরকারের প্রতি আহ্বান জানান পশ্চিমবঙ্গের মুখ্যমন্ত্রী মমতা। তার ওই বক্তব্যের প্রতিক্রিয়ায় এ কথা বলেন বাংলাদেশের অন্তর্বর্তী সরকারের পররাষ্ট্র উপদেষ্টা। সোমবার বিকালে রাষ্ট্রীয় অতিথি ভবন পদ্মায় সাংবাদিকদের প্রশ্নের জবাবে তৌহিদ হোসেন বলেন, ‘মমতা ব্যানার্জির বক্তব্যকে মমতা ব্যানার্জির ধরনের হিসেবে দেখতে চাই।’ মমতাকে চেনাজানা এবং তার বাড়িতে যাওয়ার অভিজ্ঞতা রয়েছে জানিয়ে উপদেষ্টা বলেন, মমতা ব্যানার্জি কেন এমন বক্তব্য দিলেন, আমরা জানি না। ব্যক্তিগতভাবে মনে করি, তার রাজনীতির জন্য বিষয়টি ঠিক হয়নি। পারস্পরিক স্বার্থের ভিত্তিতে ভারতের সঙ্গে আমরা স্বাভাবিক সম্পর্ক রাখতে চাই।’ এ সময় চিন্ময় কৃষ্ণ দাশের গ্রেপ্তার নিয়ে পররাষ্ট্র উপদেষ্টা বলেন, তাকে গ্রেপ্তার এবং আইনি প্রক্রিয়ার বিষয়ে বিদেশি কূটনীতিকদের ব্যাখ্যা দেয়া হয়েছে। সঙ্গে বলা হয়েছে, তার অনুসারীরা বিক্ষোভ করতে পেরেছেন। কারণ, এই স্বাধীনতা নিশ্চিত করেছে অন্তর্বর্তী সরকার। বাংলাদেশ পরিস্থিতি নিয়ে একটি বিশেষ গোষ্ঠী বৈশ্বিক প্রচারণা চালাচ্ছে উল্লেখ করে তৌহিদ হোসেন বলেন, ‘তারা অপপ্রচার চালাচ্ছে এবং এক্ষেত্রে তারা সর্বশক্তি নিয়োগ করেছে। আমরা মেনে নিচ্ছি, তাদের এক্ষেত্রে শক্তি আমাদের চেয়ে বেশি। তবে আমরা সবাইকে বিষয়টি স্পষ্ট করার চেষ্টা করছি।’ ভারতীয় গণমাধ্যম বাংলাদেশের সংখ্যালঘু পরিস্থিতি নিয়ে মিথ্যা তথ্য ছড়াচ্ছে উল্লেখ করে পররাষ্ট্র উপদেষ্টা বলেন, ‘প্রধানত ভারতীয় গণমাধ্যমে অপপ্রচার চাালানো হচ্ছে। এর বাইরেও অনেক গণমাধ্যম ভারতীয় গণমাধ্যমকে উপজীব্য করে অপতথ্য ছড়াচ্ছে। তবে আমরা বলতে চাই, সব সরকারের আমলেই বছরে দু’-একটা বিচ্ছিন্ন ঘটনা ঘটে। তবে সরকারের কাজ পরিস্থিতি নিয়ন্ত্রণ করা। আমরা সেটি করেছি। তবে দেশ ও দেশের বাইরে এ নিয়ে মিথ্যা তথ্য ও বিভ্রান্তি ছড়ানো হচ্ছে।’ দেশের আইনশৃঙ্খলা পরিস্থিতি স্বাভাবিক রাখতে সরকার বদ্ধপরিকর জানিয়ে উপদেষ্টা তৌহিদ হোসেন বলেন, ‘আমরা বার্তা দিতে চাই, বর্তমান সরকার সামপ্রদায়িক কোনো অপতৎপরতা বরদাশ্‌ত করবে না। আমরা হিন্দু-মুসলিম কোনো ভেদ করতে চাই না। কোনো বিশৃঙ্খলা দেখা গেলে আমরা আইনানুগ ব্যবস্থা নেবো।’\nঅন্যান্য প্রসঙ্গ: এক প্রশ্নের জবাবে তৌহিদ হোসেন বলেন, ‘বৃটেনের অল পার্টি পার্লামেন্টারি গ্রুপের প্রতিবেদন একপেশে হয়েছে। প্রতিবেদন তৈরিতে সেখানে অবস্থানরত একটি বিশেষ গোষ্ঠীর মানুষ প্রভাব রেখেছে। বৃটেনের আওয়ামীপন্থি সাংসদের সংখ্যাও বেশি। তারা প্রতিবেদনকে প্রভাবিত করেছে। তারা আমাদের দূতাবাসকে এ বিষয়ে কিছু জানায়নি। ভারতের সঙ্গে সম্পর্কে টানাপড়েন চলছে এ বিষয়ে জানতে চাইলে উপদেষ্টা বলেন, আসলেই ৫ই আগস্টের পর থেকে দুই দেশের সম্পর্কে সমস্যা চলছে। এটা স্বীকার করতেই হবে। তবে আমরা স্বাভাবিক, ভালো সুসম্পর্ক চাই। এই সম্পর্ক অন্তরের চেয়ে স্বার্থই বেশি গুরুত্বপূর্ণ। দুই দেশের সম্পর্ক স্বার্থের মধ্যদিয়ে দেখতে হবে। তিনি বলেন, আমি কাল একটা হাসপাতালে গেলাম দেখি রোগী ভর্তি। আর কলকাতার দোকানপাট এখন খালি, যেহেতু ভিসা বন্ধ। এখন ভারতের স্বার্থ তো আমি বুঝতে পারবো না। তারা তাদের স্বার্থ বুঝবে। উপদেষ্টা বলেন, হিন্দু সমাজের পরিস্থিতি নিয়ে আমাদের মনে হয়েছিল ভুল ধারণা যাতে সৃষ্টি না হয়, ভুল ধারণা সৃষ্টি করার সুযোগ আছে। বিশেষ করে মিডিয়া, কোন দেশের মিডিয়া বলছি না। তারা যতটুকু পারা যায় খারাপ পরিস্থিতির দিকে নিয়ে যাওয়ার চেষ্টা করেছে। কাজেই আমাদের মনে হয়েছে যে কূটনীতিক যে প্রতিনিধিরা আছেন তাদের ব্যাখ্যা দেয়া প্রয়োজন যাতে তারা প্রকৃত অবস্থা বুঝতে পারে। তাদের কিছু ম্যাকানিজম আছে, কিন্তু আমরা আমাদের অবস্থান ব্যাখ্যা করেছি। তৌহিদ হোসেন বলেন, আমরা বলেছি সাম্প্রদায়িক সম্প্রীতি আমাদের সমাজের অংশ। সরকার এটি বজায় রাখতে দৃঢ় প্রতিজ্ঞ। কোন মানুষ তার ধর্মীয় পরিচয়ের কারণে নিগৃহীত হবে না সেটা আমরা নিশ্চিত করবো। সরকার এক্ষেত্রে উল্লেখযোগ্য সাফল্য দেখিয়েছে গত চার মাসে। যদিও অনেক মাল-মসলা ছিল অনেক বেশি গণ্ডগোল হওয়ার মতো। সেটি আমরা নিয়ন্ত্রণে রাখতে পেরেছি। দুর্গাপূজার মতো উৎসব শান্তিপূর্ণ হয়েছে। এক-দুইটি ঘটনা যে ঘটেনি তেমনটা তো না। সেগুলো প্রতি বছরই দুই-একটা ঘটে। কোন দেশের মিডিয়ার অপপ্রচারের কথা বলছেন এমন প্রশ্নের জবাবে তিনি বলেন, এ ব্যাপারে আমি খুব স্পষ্ট। প্রধানত ভারতের মিডিয়া। কিন্তু এর বাইরেও অনেক মিডিয়া ভারতের মিডিয়ার বক্তব্য ধরে নিয়ে প্রচার করেছে। মিডিয়া ছাড়াও ভারতের সরকার বা রাজনীতিবিদদের কোনো কড়া বার্তা দিতে চান কিনা? এমন প্রশ্নের জবাবে তিনি বলেন, আমরা যে বার্তাটা দিতে চাই এই সরকার কোন ধরনের কোন সাম্প্রদায়িক কার্যকলাপ বরদাশত করবে না। সেটা হিন্দু বা মুসলিম বলে কথা নয়। আমরা সবাইকে সমান চোখে দেখবো। এই বার্তাটি আমরা সবাইকে দিতে চেয়েছি। এ ব্যাপারে আইন তার গতিতে চলবে। যদি কেউ বিশৃঙ্খলা সৃষ্টির চেষ্টা করে তাদের শক্ত হাতে দমন করা হবে। এই বার্তাটি সবার কাছে গেছে। সরকারিভাবে কোনো প্রতিক্রিয়া দেখলে সেটি সরকারিভাবেই প্রতিবাদ জানাই। বাকিটুকু সাধারণভাবেই বলেছি। তিনি বলেন, আজকের ব্রিফিংয়ে ভারতকে নিয়ে সেভাবে কিছু বলিনি। আমি মিডিয়ার সম্পর্কে বলেছি। বৈঠকে ভারতীয় দূতাবাসের প্রতিনিধি উপস্থিত ছিলেন। এক প্রশ্নের জবাবে তিনি বলেন, এটা ভুলে গেলে চলবে না যে একটা গ্লোবাল ক্যাম্পেইন চলছে। সবাই পার্টিসিপেট করছে তা না। কিন্তু গ্লোবাল ক্যাম্পেইনটা চলছে একটা বিশেষ গোষ্ঠীর কারণে। চেষ্টা করছে সব জায়গায় হিট করার। এতে কোনো সন্দেহ নেই। এটাও মেনে নিতে হবে তাদের রিচ আমাদের তুলনায় বেশি, আমাদের তাদের মোকাবিলা করতে হবে। বর্ডারে ভারতীয় প্রতিবাদকারীরা বাংলাদেশের বর্ডারে ঢুকে যাওয়ার চেষ্টা করেছে, সেটিকে কীভাবে দেখেন- এমন প্রশ্নে উপদেষ্টা বলেন, এটাকে আমি একটা প্রভোকেশন হিসেবেই দেখি। প্রভোকেশন দেয়ার মতো লোকজন তো আছে, সেটা আমরা দেখতে পাচ্ছি। এটাকে প্রভোকেশন হিসেবে ততক্ষণ দেখবো যতক্ষণ না তারা বর্ডার অতিক্রম করতে সক্ষম হয়। বিএসএফ তাদের সেখানে থামাতে সক্ষম হয়েছে। প্রোটেস্ট তারা করতেই পারেন, তাদের দেশের আইনও সেটাকে অ্যালাউ করে। তবে বর্ডার পার হওয়া মানে আইন ভঙ্গ করা। সেটা তারা ব্যবস্থা নিবেন। ভারতের সঙ্গে বাংলাদেশ বন্ধুত্বপূর্ণ ও স্বাভাবিক সম্পর্ক চায় জানিয়ে পররাষ্ট্র উপদেষ্টা বলেন, পারস্পরিক স্বার্থ রক্ষা করে ভারতের সঙ্গে স্বাভাবিক ও বন্ধুত্বপূর্ণ সম্পর্ক দেখতে চায় বাংলাদেশ। চলতি মাসে নির্ধারিত ফরেন অফিস কনসালটেশন (এফওসি) অনুষ্ঠিত হবে কিনা জানতে চাইলে তিনি বলেন, ‘আমি মনে করি এটি অনুষ্ঠিত হবে।’', '2024-12-03 00:00:00', '2024-12-03 03:47:59', '[\"https://mzamin.com/uploads/news/main/138378_Untitled-52.webp\"]', 1813, '0d5ea60442738f49076b399f2100d8ed');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746220, 2268, 'https://mzamin.com/news.php?news=138305', 'https://mzamin.com/news.php?news=138305', 'সংবাদ', NULL, 'অবসরের ঘোষণা', 'টেলিপর্দা থেকে সফর শুরু করে বড় পর্দাতেও নিজের জাত চিনিয়েছেন অভিনেতা বিক্রান্ত ম্যাসি', 'টেলিপর্দা থেকে সফর শুরু করে বড় পর্দাতেও নিজের জাত চিনিয়েছেন অভিনেতা বিক্রান্ত ম্যাসি\nটেলিপর্দা থেকে সফর শুরু করে বড় পর্দাতেও নিজের জাত চিনিয়েছেন অভিনেতা বিক্রান্ত ম্যাসি। এবার অভিনয় থেকে অবসরের ঘোষণা দিলেন ৩৭ বছর বয়সী এই অভিনেতা। রোববার সোশ্যাল মিডিয়ায় একথা জানিয়ে তিনি লিখেন, গত কয়েক বছরের সফর সত্যিই অসাধারণ। পাশে থাকার জন্য আপনাদের ধন্যবাদ জানাচ্ছি। এবার ঘরে ফেরার সময় হয়েছে একজন স্বামী কিংবা বাবা হিসেবে।', '2024-12-03 00:00:00', '2024-12-03 03:47:59', '[\"https://mzamin.com/uploads/news/main/138305_vikrant.webp\"]', 1813, '0ed9b59039897a950e6a19aa51502e88');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746221, 2268, 'https://mzamin.com/news.php?news=138377', 'https://mzamin.com/news.php?news=138377', 'সংবাদ', NULL, 'সীমান্তে অচলাবস্থা  বাণিজ্য ব্যাহত', 'বাংলাদেশে সংখ্যালঘুদের ওপর নির্যাতনের অভিযোগে এবং গ্রেপ্তার হওয়া সন্ন্যাসীদের মুক্তির দাবিতে গতকাল পশ্চিমবঙ্গ ও আসামের বাংলাদেশ সীমান্তে অবরোধ কর্মসূচি পালিত হয়েছে', 'বাংলাদেশে সংখ্যালঘুদের ওপর নির্যাতনের অভিযোগে এবং গ্রেপ্তার হওয়া সন্ন্যাসীদের মুক্তির দাবিতে গতকাল পশ্চিমবঙ্গ ও আসামের বাংলাদেশ সীমান্তে অবরোধ কর্মসূচি পালিত হয়েছে। ফলে দুই দেশের মধ্যে দ্বিপক্ষীয় বাণিজ্য ব্যাহত হয়েছে বলে বিভিন্ন সূত্রে জানা গেছে। ওদিকে সিলেটের শ্যাওলা ও জকিগঞ্জ পোর্ট দিয়ে বাণিজ্য কার্যক্রম বন্ধ রয়েছে।\nপশ্চিমবঙ্গের উত্তর ২৪ পরগনার পেট্রাপোলে এদিন প্রায় হাজার খানেক সন্ন্যাসী ও বিভিন্ন হিন্দু সংগঠনের সমর্থকরা সীমান্ত অবরুদ্ধ করে রাখেন। সকাল থেকেই রাজ্যের বিভিন্ন জায়গা থেকে মানুষ সীমান্তে সমবেত হয়েছিলেন। পুলিশ অবশ্য সতর্কতা হিসেবে তাদের সীমান্তের জিরো পয়েন্টের ৮০০ মিটার আগেই আটকে দিয়েছে। অবশ্য আন্দোলনকারীরা মানববন্ধন করে জিরো পয়েন্টে গিয়ে বাংলাদেশকে বার্তা দিতে চলেছেন। অখিল ভারতীয় সন্ত সমিতির আয়োজনে এই অবরোধ কর্মসূচি থেকে বাংলাদেশে হিন্দুদের ওপর নির্যাতন বন্ধের জন্য প্রধানমন্ত্রী নরেন্দ্র মোদির হস্তক্ষেপ চাওয়া হয়েছে।\nসন্ত সমিতির বাংলা কমিটির সভাপতি স্বামী পরমাত্মানন্দ হুঁশিয়ারি দিয়ে বলেছেন, নির্যাতন বন্ধ এবং ধৃত সন্ন্যাসীদের মুক্তি না দেয়া পর্যন্ত সীমান্ত অবরোধ কর্মসূচি চলবে। তিনি বলেন, আমরা সব রকমের বাণিজ্য বন্ধ করে দেবো। কোচবিহারের চ্যাংড়াবান্ধা সীমান্তেও এদিন কয়েক শ’ মানুষ প্রতিবাদে বিক্ষোভ দেখিয়েছেন। সীমান্তে এই অবরোধের ফলে দুই দেশের মধ্যে যাত্রীদের আনাগোনা এক প্রকার ছিল না বললেই চলে।বিজেপি’র নেতা ও রাজ্য বিধানসভার বিরোধী দলনেতা শুভেন্দু অধিকারী আগামী বুধবার পেট্রাপোল সীমান্ত অবরুদ্ধ করে দেবেন বলে ঘোষণা দিয়েছেন।এদিকে আসামের সীমান্তেও বিক্ষোভ কর্মসূচি পালিত হয়েছে। সংবাদ সূত্রে জানা গেছে, সনাতনী ঐক্যমঞ্চ’র ডাকে এই বিক্ষোভ কর্মসূচিতে কয়েক হাজার মানুষ অংশ নিয়েছিলেন। শিবসাগর জেলার সুতারকান্দি সীমান্তে চলো বাংলাদেশ নামের এই বিক্ষোভ কর্মসূচিকে সমর্থন জানিয়েছেন আসামের মুখ্যমন্ত্রী হিমন্ত বিশ্বশর্মা।\nএদিকে, রাষ্ট্রীয় স্বয়ংসেবক সংঘের (আরএসএস) সাধারণ সম্পাদক দত্তাত্রেয় হোসাবলে এক বিবৃতিতে ভারত সরকারের কাছে বাংলাদেশে হিন্দু এবং অন্যান্য সংখ্যালঘুদের ওপর অত্যাচার বন্ধ করার প্রচেষ্টা চালিয়ে যাওয়া এবং তাদের সমর্থনে বিশ্বব্যাপী জনমত গড়ে তোলার জন্য যত তাড়াতাড়ি সম্ভব প্রয়োজনীয় পদক্ষেপ নেয়ার আবেদন জানিয়েছেন। তিনি বলেন, বাংলাদেশে হিন্দু, নারী ও অন্যান্য সংখ্যালঘুদের ওপর ইসলামী মৌলবাদীদের হামলা, হত্যা, লুটপাট, অগ্নিসংযোগ ও অমানবিক নৃশংসতার ঘটনা অত্যন্ত উদ্বেগজনক। হিন্দু মহাসভার পশ্চিমবঙ্গ কমিটির সভাপতি ডক্টর চন্দ্রচূড় গোস্বামী বলেছেন, আমরা প্রতিবাদ করছি। তবে প্রতিশোধ নেয়া প্রয়োজন।ওদিকে সিলেট সীমান্তে ভারতের ‘হিন্দু ঐক্যমঞ্চ’র বাংলাদেশ অভিমুখে মার্চের পর দুই পোর্টে বাণিজ্য কার্যক্রম বন্ধ রয়েছে। এ কারণে গতকাল বিয়ানীবাজারের শ্যাওলা ও জকিগঞ্জ পোর্ট দিয়ে মালামাল আসা-যাওয়া করেনি। ব্যবসায়ীরা জানিয়েছেন; ভারত অংশে উত্তেজনার কারণে আপাতত বন্ধ রয়েছে দুই পোর্ট। বাংলাদেশে কথিত সংখ্যালঘু নির্যাতনের অভিযোগে ভারতের আসাম রাজ্যের করিমগঞ্জ এলাকায় বিক্ষোভ চলছে। ভারতের হিন্দু ঐক্যমঞ্চ নামে একটি সংগঠনের ব্যানারে রোববার কয়েকশ’ সনাতনী মানুষ ‘বাংলাদেশ চলো’ কর্মসূচি নিয়ে বিয়ানীবাজারের শ্যাওলা স্থলবন্দর অভিমুখে আসতে থাকে। বাংলাদেশ সীমান্তের অদূরে একটি স্থানে প্রথমে পুলিশ তাদের বাধা দেয়। কিন্তু পুলিশের বাধা ডিঙিয়ে তারা এগুতে থাকলে ভারতের সীমান্তরক্ষী বাহিনী বিএসএফ কাঁটা তার দিয়ে ব্যারিকেড দেয়। এই ব্যারিকেড ভাঙতে চাইলে ভারতের উগ্রপন্থি সনাতনীদের সঙ্গে বিএসএফ ও ওখানকার পুলিশের সংঘর্ষ হয়। সামাজিক যোগাযোগ মাধ্যমে ছড়িয়ে পড়া ভিডিওতে দেখা গেছে- সংঘর্ষে বেশ কয়েকজন আহত হন। তবে ব্যারিকেড ভেঙে শেষ পর্যন্ত আরএসএ’র নেতৃত্বে থাকা ‘হিন্দু ঐক্যমঞ্চ’র নেতারা সীমান্ত এলাকায় আসতে পারেননি। তবে; তাদের এই মুভমেন্টের পর বাংলাদেশের শ্যাওলা সীমান্ত বন্দর দিয়ে বাণিজ্যিক কার্যক্রম বন্ধ করে দেয়া হয়। পোর্ট কর্মকর্তারা জানিয়েছেন- নিরাপত্তা বিবেচনায় কার্যক্রম বন্ধ রাখা হয়েছে। এর ধারাবাহিকতায় গতকালও পোর্টের কার্যক্রম বন্ধ ছিল। ‘বাংলাদেশ চলো’ মুভমেন্টে স্থানীয় ‘হিন্দু ঐক্যমঞ্চ’র নেতারা বলছিলেন; ‘জীবন গেলে যাবে, তবুও তারা বাংলাদেশে যাবেন।’ বাংলাদেশে সংখ্যালঘুদের নির্যাতনের প্রতিবাদে তারা এই মার্চ কর্মসূচি পালন করছেন বলে জানান। এদিকে- আসাম রাজ্যের করিমগঞ্জের সুতারকান্দি সীমান্তে যখন মুভমেন্ট নিয়ে উত্তেজনা বিরাজ করছিল তখন রোববার পার্শ্ববর্তী করিমগঞ্জ স্টিমারঘাটের ব্যবসায়ীরা বাংলাদেশের সঙ্গে আমদানি- রপ্তানি বন্ধ রাখার ঘোষণা দেন। করিমগঞ্জের মিডিয়ায় ছড়িয়ে পড়া খবরে জানা গেছে; ভারত অংশের ব্যবসায়ীরা বাংলাদেশ পরিস্থিতি বিবেচনায় রেখে এই সিদ্ধান্ত নেন। এ সময় বাংলাদেশে কঠিন পরিস্থিতি চলছে বলে ব্যবসায়ী নেতারা উল্লেখ করেন। তবে; ব্যবসায়ীদের এই সিদ্ধান্ত না মেনে গতকাল সকালে করিমগঞ্জ পোর্ট দিয়ে কার্যক্রম শুরু হলে করিমগঞ্জ এলাকার স্থানীয় বিধায়ক কমলাক্ষ চক্রবর্তীর নেতৃত্বে করিমগঞ্জের কিছু সংখ্যক নেতাকর্মী পোর্টে এসে ক্ষোভ প্রকাশ করেন। এ সংক্রান্ত একটি ভিডিও গতকাল দুপুরের পর জকিগঞ্জে ভাইরাল হয়েছে। ওই ভিডিওতে দেখা গেছে; বিধায়ক কমলাক্ষ চক্রবর্তী কিছু সংখ্যক কর্মী নিয়ে এসে নদী তীরবর্তী স্টিমারঘাটের পোর্টে আসেন। এ সময় ওই পোর্ট দিয়ে বাংলাদেশ থেকে যাওয়া কিছু মালামাল ঢুকছিল। তিনি মালামালের একটি চালান আটক করে প্রকাশ্যে আগুন ধরিয়ে দেন। এক পর্যায়ে তিনি নিজেই বাঁশ এনে পোর্ট এলাকায় ব্যারিকেড দেন। একই সঙ্গে ঘোষণা দেন; বাংলাদেশ পরিস্থিতি স্বাভাবিক না হওয়া পর্যন্ত পোর্ট দিয়ে আমদানি- রপ্তানি বন্ধ থাকবে। তবে এই মুভমেন্টের আগে দু’দেশের মধ্যে কয়েক গাড়ি মালামাল আসা-যাওয়া করেছে বলে জানিয়েছেন ব্যবসায়ীরা। বাংলাদেশের জকিগঞ্জ পোর্টের কর্মকর্তারা জানিয়েছেন- সকালে করিমগঞ্জ থেকে নদীপথে দুই ট্রাক পণ্য এসেছিল। ওই অংশে কিছু বিশৃঙ্খলার কারণে বিকাল পর্যন্ত মালামাল আসেনি ও বাংলাদেশ থেকে যায়নি। সিলেটের ব্যবসায়ী নেতারা জানিয়েছেন; ভারত অংশের সমস্যার কারণে বিয়ানীবাজারের শ্যাওলা ও জকিগঞ্জ পোর্ট দিয়ে বাণিজ্যিক কার্যক্রম বন্ধ রয়েছে। তবে বাংলাদেশের পরিস্থিতি স্বাভাবিক রয়েছে। ভারতের ব্যবসায়ীদের তরফ থেকে যখনই সম্মতি আসবে তখনই ফের বাণিজ্যিক কার্যক্রম শুরু করা হবে। সিলেট আমদানিকারক গ্রুপের যুগ্ম সম্পাদক ও শ্যাওলা বন্দরের ব্যবসায়ী জয়ন্ত চক্রবর্তী মানবজমিনকে জানিয়েছেন- করিমগঞ্জের সুতারকান্দি স্থল বন্দরের ঝামেলার কারণে রোববার থেকে সিলেটের শ্যাওলা ও জকিগঞ্জ পোর্ট দিয়ে আমদানি-রপ্তানি বন্ধ রয়েছে। করিমগঞ্জের পরিস্থিতি স্বাভাবিক হলে আমদানি-রপ্তানি শুরু হতে পারে বলে জানান তিনি। শ্যাওলা পোর্টের সিবিএ’র সাধারণ সম্পাদক নিয়াজ উদ্দিন জানিয়েছেন- ‘হিন্দু ঐক্যমঞ্চ’র মুভমেন্টের আগে আমদানি-রপ্তানি স্বাভাবিক ছিল। এজন্য পোর্টে মালামাল লোড করা ট্রাক অপেক্ষমাণ ছিল। বাণিজ্যিক কার্যক্রম বন্ধ হওয়ার কারণে ওই ট্রাকগুলো আটকা পড়ে। আপাতত লোড করা ট্রাকগুলো খালাস করার চেষ্টা করা হচ্ছে। তবে; বাংলাদেশ অংশে পরিস্থিতি স্বাভাবিক রয়েছে। স্থানীয়দের মধ্যেও এ নিয়ে কোনো উত্তেজনা নেই। বাংলাদেশ বর্ডার গার্ড বিজিবি’র ১৯ ব্যাটালিয়নের কমান্ডিং অফিসার লে. কর্নেল মেহেদী হাসান জানিয়েছেন- বাংলাদেশ অংশের পরিস্থিতি স্বাভাবিক। সীমান্তে পূর্বের মতো বিজিবি’র পক্ষ থেকে টহল জোরদার রয়েছে। জকিগঞ্জ উপজেলার নির্বাহী কর্মকর্তা আফসানা তাসনিম জানিয়েছেন- সীমান্তে নজরদারি চলছে। একই সঙ্গে বিজিবি’র পক্ষ থেকে অতিরিক্ত লোকবল নিয়ে টহল বাড়ানো হয়েছে।', '2024-12-03 00:00:00', '2024-12-03 03:48:07', '[\"https://mzamin.com/uploads/news/main/138377_2.webp\"]', 1813, 'a80913b2a712ad21550e5ee115c927bd');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746222, 2268, 'https://mzamin.com/news.php?news=138316', 'https://mzamin.com/news.php?news=138316', 'সংবাদ', NULL, 'এসএ গেমসে স্বর্ণজয়ী শুটার সাদিয়া আর নেই', 'প্রায় এক যুগ ধরে অনেকটা অন্তরালে ছিলেন সোনাজয়ী শুটার সাদিয়া সুলতানা', 'প্রায় এক যুগ ধরে অনেকটা অন্তরালে ছিলেন সোনাজয়ী শুটার সাদিয়া সুলতানা\nপ্রায় এক যুগ ধরে অনেকটা অন্তরালে ছিলেন সোনাজয়ী শুটার সাদিয়া সুলতানা। মাঝে ২০১৭ সালে আগুনে পুড়ে অসুস্থ ছিলেন দীর্ঘদিন। গতকাল তিনি পৃথিবীর মায়া ছেড়ে পাড়ি জমিয়েছেন অন্যলোকে। চট্টগ্রামের একটি হাসপাতালে আইসিইউতে চিকিৎসাধীন অবস্থায় মৃত্যুবরণ করেন সাদিয়া।দীর্ঘদিন ধরে মানসিকভাবে বিপর্যস্ত ছিলেন এই শুটার। গতকাল সবার অলক্ষ্যে বাড়ির ছাদ থেকে লাফ দেন তিনি। এরপর আহত সাদিয়াকে পরিবারের সদস্যরা চট্টগ্রাম মেডিকেলে ভর্তি করেন। সেখানেই চিকিৎসাধীন অবস্থায় মারা যান তিনি। সাদিয়ার শুটিং ক্যারিয়ার বর্ণাঢ্য। ২০১০ সালের কমনওয়েলথ শুটিং চ্যাম্পিয়নশিপে ১০ মিটার এয়ার রাইফেলে সোনা জেতেন। দলগত ইভেন্টে দিল্লির আসর থেকে শারমিন আক্তার রত্নার সঙ্গে জুটি গড়েও জেতেন সোনা। তার আগে ওই বছরই এসএ গেমসে একই ইভেন্টে জেতেন সোনার পদক। সবশেষ ২০১৩ সালে ১০ মিটার এয়ার রাইফেল থেকে সাফল্য পান বাংলাদেশ গেমসে। এরপর থেকেই শুটিংয়ের বাইরে তিনি। গত ১১ বছর বলতে গেলে একেবারে আড়ালেই ছিলেন খেলার দুনিয়া থেকে। সাত বছর আগে পরিবারের পক্ষ থেকে বলা হয়েছিল সাদিয়া অসুস্থ। যদিও আর কখনও শুটিংয়ে ফেরেননি। একেবারে মুখ ফিরিয়ে নেয়ার কারণটা রহস্যই থেকে গেছে!', '2024-12-03 00:00:00', '2024-12-03 03:50:52', '[\"https://mzamin.com/uploads/news/main/138316_6.webp\"]', 1813, '90fe39961550906d973c7977b063ad6e');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746223, 2268, 'https://mzamin.com/news.php?news=138357', 'https://mzamin.com/news.php?news=138357', 'সংবাদ', NULL, 'সামনের নির্বাচন অনেক কঠিন  হতে যাচ্ছে', 'বিএনপি’র ভারপ্রাপ্ত চেয়ারম্যান তারেক রহমান বলেছেন, সামনের যে নির্বাচন হবে এত সহজ হবে না', 'বিএনপি’র ভারপ্রাপ্ত চেয়ারম্যান তারেক রহমান বলেছেন, সামনের যে নির্বাচন হবে এত সহজ হবে না। এই নির্বাচন বাংলাদেশের যেকোনো নির্বাচনের চেয়ে অনেক কঠিন হতে যাচ্ছে। মানুষের ধ্যান-ধারণার পরিবর্তন হয়েছে। কেউ যদি কোনো ভুল করে থাকে তাহলে একজন নেতা হিসেবে তাকে সঠিক পথে নিয়ে আসা আপনার দায়িত্ব। গতকাল দিনব্যাপী খুলনা প্রেস ক্লাবের ব্যাংকুয়েট হলে অনুষ্ঠিত ‘রাষ্ট্র মেরামতে বিএনপি প্রদত্ত ৩১ দফা বিষয়ক খুলনা বিভাগীয় কর্মশালায় ভার্চুয়ালি প্রধান অতিথির বক্তব্যে তিনি এসব কথা বলেন। অনুষ্ঠানে বিএনপির জাতীয় এবং খুলনা ও ময়মনসিংহ বিভাগের নেতাকর্মী অংশ নেন।\nনেতাকর্মীর বিভিন্ন প্রশ্নের জবাবে তিনি বলেন, জনগণের সমর্থন নিয়ে দেশ পরিচালনার সুযোগ পেলে ফ্যামিলি কার্ড এবং কৃষকদের জন্য ফার্মার্স কার্ড তৈরি করব। ফ্যামিলি কার্ডটি হবে পরিবারের মা বা স্ত্রীর নামে। এতে তার পারিবারিক ও সামাজিক মর্যাদা বাড়বে। একই সঙ্গে কৃষকদের জন্য ফার্মার্স কার্ড করা হবে। যা প্রতি মৌসুমে কৃষকের সার-বীজ বা আর্থিক সহযোগিতা প্রাপ্তিতে সহযোগিতা করবে।তারেক রহমান নেতাকর্মীর উদ্দেশ্যে বলেন, ক্ষমতার উৎস জনগণ। আমরা ক্ষমতায় যাইনি, যাব কিনা জানি না। তখনই ক্ষমতায় যেতে পারব, যখন জনগণের সমর্থন পাব। তিনি বলেন, দেশ সব মানুষের জন্য। গত কিছুদিন সমাজের কিছু ব্যক্তি সংস্কারের কথা বলছে। গত ৫ মাস আগেও তারা বলেনি। কিন্তু বিএনপি ২ বছর আগে থেকে বলছে। বিএনপি দেশ ও দেশের মানুষের কথা চিন্তা করে। ৩১ দফা তারই প্রমাণ। গত ৩-৪ মাস সংস্কারের যে কথা বলছে। তার সবকিছুই ৩১ দফার মধ্যে রয়েছে। এই ৩১ দফা শুধু বিএনপি’র তাই নয়, বাংলাদেশের পক্ষে গণতান্ত্রিক সব দলের।তিনি আরও বলেন, বিগত ১৭ বছর স্বৈরাচারের বিরুদ্ধে আপনারা সংগ্রাম করে যেভাবে টিকে ছিলেন, তার থেকে সহজ হবে এই কাজটা জনগণের ঘরে পৌঁছে দেওয়ার জন্য। এত কঠিন পথ যদি আপনারা পাড়ি দিয়ে আসতে সক্ষম হয়ে থাকেন। তাহলে একটু কষ্ট করলেও ৩১ দফা আমরা জনগণের মধ্যে নিয়ে যেতে পারি।\nতারেক রহমান বলেন, নারীর যোগ্যতা ও মর্যাদা আরও বৃদ্ধি করা উচিত। এটি সামাজিক আন্দোলনে পরিণত করা উচিত। খালেদা জিয়া ক্ষমতায় এসে নারীদের জন্য শিক্ষা ব্যবস্থা ফ্রি করে দিয়েছিলেন। প্রতিটি পরিবারের জন্য একটি ফ্যামিলি কার্ড তৈরি করব। প্রান্তিক মানুষকে রাষ্ট্রের পক্ষ থেকে মিনিমাম সহযোগিতা করার চেষ্টা করব। তিনি বলেন, আমাদের পরিকল্পনার মধ্যে রয়েছে বৃক্ষরোপণ, নদী ও খাল খনন করতে হবে। লেখাপড়ার পাশাপাশি ছেলেমেয়েকে খেলাধুলায়ও এগিয়ে নেয়া হবে। এসময় তিনি ৩১ দফার বিভিন্ন বিষয় তুলে ধরেন নেতাকর্মীর কাছে।বিএনপি’র খুলনা বিভাগীয় ভারপ্রাপ্ত সাংগঠনিক সম্পাদক আনিন্দ্য ইসলাম অমিতের সভাপতিত্বে কর্মশালায় ছিলেন বিএনপির মিডিয়া সেলের আহ্বায়ক ড. মওদুদ আলমগীর পাভেল, বিএনপি’র প্রশিক্ষণ বিষয়ক সম্পাদক এবিএম মোশাররফ হোসেন, তথ্যবিষয়ক সম্পাদক আজিজুল বারী হেলাল, প্রচার সম্পাদক সুলতান সালাউদ্দিন টুকু, ছাত্রবিষয়ক সম্পাদক রকিবুল ইসলাম বকুল, তথ্য ও গবেষণা সম্পাদক শামীমুর রহমান শামীম, খুলনা বিভাগীয় সহ-সাংগঠনিক সম্পাদক বাবু জয়ন্ত কুমার কুণ্ড, সহ-ত্রাণ ও পুনর্বাসন সম্পাদক এডভোকেট নেওয়াজ হালিমা আরলী প্রমুখ। কর্মশালায় খুলনা বিভাগের ১১টি ইউনিটের নেতাকর্মী অংশগ্রহণ করেন।\nএদিকে ময়মনসিংহ বিভাগের ৭টি ইউনিটের ৫১২ প্রশিক্ষণার্থী নেতাদের অংশগ্রহণে এই প্রশিক্ষণ কর্মশালার উদ্বোধন করেন দলের স্থায়ী কমিটির সিনিয়র সদস্য মো. নজরুল ইসলাম খান। এ সময় বিএনপি’র জাতীয় নির্বাহী কমিটির সাংগঠনিক সম্পাদক (ময়মনসিংহ বিভাগ) মো. শরীফুল আলমের সভাপতিত্বে আলোচক ছিলেন বিএনপি’র যুগ্ম মহাসচিব সৈয়দ এমরান সালেহ প্রিন্স, চেয়ারপার্সনের উপদেষ্টা ইসমাঈল জবি উল্লাহ, দলের ভারপ্রাপ্ত চেয়ারম্যানের উপদেষ্টা ডা. মাহাদী আমিন, বিএনপি’র কোষাধ্যক্ষ রশিদুজ্জামান মিল্লাত, বিভাগীয় সহসাংগঠনিক সম্পাদক- ওয়ারেস আলী মামুন, আবু ওয়াহাব আকন্দ ওয়াহিদ, সাবেক এমপি রেহানা আক্তার রানু, কেন্দ্রীয় কৃষকদলের যুগ্ম সাধারণ সম্পাদক মাহমুদা হাবীবা। এ সময় কর্মশালাটি যৌথভাবে সঞ্চালনা করেন- বিএনপি’র জাতীয় নির্বাহী কমিটির সহগণশিক্ষা বিষয়ক সম্পাদক আনিসুর রহমান তালুকদার খোকন এবং নির্বাহী কমিটির সদস্য (দপ্তরে সংযুক্ত) আব্দুস সাত্তার পাটোয়ারী। এ ছাড়া প্রশিক্ষণ কর্মশালায় বিএনপি’র কেন্দ্রীয় নেতাদের মধ্যে আরও ছিলেন- মোস্তাফিজুর রহমান বাবুল, সাবেক এমপি নিলুফার চৌধুরী মনি, মাহমুদুল হাসান রুবেল, ডা. মাহাবুবুর রহমান লিটন, লাইলী বেগম, ড. শামছুজ্জামান মেহেদী, আরিফা জেসমিন, ময়মনসিংহ মহানগর বিএনপি’র আহ্বায়ক অধ্যাপক শফিকুল ইসলাম, দক্ষিণ জেলা বিএনপি’র আহ্বায়ক জাকির হোসেন বাবলু, উত্তর-জেলা বিএনপি’র আহ্বায়ক অধ্যাপক এনায়েত উল্লাহ কালাম, মহানগর বিএনপি’র যুগ্ম আহ্বায়ক অধ্যাপক শেখ আমজাদ আলী, দক্ষিণ জেলা বিএনপি’র সিনিয়র যুগ্ম আহ্বায়ক আলমগীর মাহমুদ আলম, উত্তর-জেলা বিএনপি’র সদস্য সচিব মোতাহার হোসেন তালুকদার, দক্ষিণ জেলা বিএনপি’র সদস্য সচিব রোকনুজ্জামান সরকার রোকন, মহানগর বিএনপি’র যুগ্ম আহ্বায়ক এম.এ হান্নান খান, শামীম আজাদ, শাহ শিব্বির আহম্মেদ ভুলু, লিটন আকন্দ, কায়কোবাদ মামুনসহ ময়মনসিংহ, জামালপুর, নেত্রকোনা, শেরপুর ও কিশোরগঞ্জ বিএনপি ও অঙ্গসংগঠনের নেতারা।', '2024-12-03 00:00:00', '2024-12-03 03:51:00', '[\"https://mzamin.com/uploads/news/main/138357_4.webp\"]', 1813, 'decc7803c914ee55c261ea45865e329f');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746224, 2268, 'https://mzamin.com/news.php?news=138356', 'https://mzamin.com/news.php?news=138356', 'সংবাদ', NULL, 'ডেঙ্গুতে আরও ৩ জনের মৃত্যু', 'রোববার সকাল ৮টা থেকে সোমবার সকাল ৮টা পর্যন্ত ২৪ ঘণ্টায় ডেঙ্গু আক্রান্ত হয়ে আরও ৩ জনের মৃত্যু হয়েছে', 'রোববার সকাল ৮টা থেকে সোমবার সকাল ৮টা পর্যন্ত ২৪ ঘণ্টায় ডেঙ্গু আক্রান্ত হয়ে আরও ৩ জনের মৃত্যু হয়েছে\nরোববার সকাল ৮টা থেকে সোমবার সকাল ৮টা পর্যন্ত ২৪ ঘণ্টায় ডেঙ্গু আক্রান্ত হয়ে আরও ৩ জনের মৃত্যু হয়েছে। এই সময়ে ডেঙ্গু আক্রান্ত হয়ে হাসপাতালে ভর্তি হয়েছেন আরও ৭০৫ জন। প্রাণঘাতী এই রোগে চলতি বছর ৪৯৭ জনের মৃত্যু হলো। সোমবার স্বাস্থ্য অধিদপ্তরের হেলথ ইমার্জেন্সি অপারেশন সেন্টার ও কন্ট্রোল রুম থেকে পাঠানো এক সংবাদ বিজ্ঞপ্তিতে এই তথ্য জানানো হয়। এতে বলা হয়েছে, ২৪ ঘণ্টায় নতুন করে ৭০৫ জন হাসপাতালে ভর্তি হয়েছেন। এর মধ্যে বরিশাল বিভাগে ৬০ জন, চট্টগ্রাম বিভাগে ৭৩ জন, ঢাকা বিভাগে (সিটি করপোরেশনের বাইরে) ১৫৯ জন, ঢাকা উত্তর সিটি করপোরেশনে ১৫৪ জন এবং দক্ষিণ সিটি করপোরেশনে ৯০ জন, খুলনা বিভাগে ৯৬ জন, রাজশাহী বিভাগে ৪৩ জন, ময়মনসিংহ বিভাগে ১৮ জন, রংপুর বিভাগে ১০ জন এবং সিলেট বিভাগে ২ জন ভর্তি হয়েছেন।এদিকে, একদিনে সারা দেশে ৭৯১ জন ডেঙ্গু রোগী হাসপাতাল থেকে ছাড়পত্র পেয়েছেন। এ নিয়ে চলতি বছর ছাড়পত্র পেয়েছেন ৮৯ হাজার ৭৮০ জন। এ বছরের জানুয়ারি থেকে এখন পর্যন্ত সবমিলিয়ে ৯৩ হাজার ৫৬ জন রোগী হাসপাতালে ভর্তি হয়েছেন।', '2024-12-03 00:00:00', '2024-12-03 03:51:04', '[]', 1813, '40f042219d7be13a240bf3cc9981b8bc');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746225, 2268, 'https://mzamin.com/news.php?news=138355', 'https://mzamin.com/news.php?news=138355', 'সংবাদ', NULL, '১৫ই আগস্টের ছুটির রায় স্থগিত', '১৫ই আগস্টকে ‘জাতীয় শোক’ দিবস ও ছুটি ঘোষণা সংক্রান্ত হাইকোর্টের রায় স্থগিত করেছেন সুপ্রিম কোর্টের আপিল বিভাগ', '১৫ই আগস্টকে ‘জাতীয় শোক’ দিবস ও ছুটি ঘোষণা সংক্রান্ত হাইকোর্টের রায় স্থগিত করেছেন সুপ্রিম কোর্টের আপিল বিভাগ\n১৫ই আগস্টকে ‘জাতীয় শোক’ দিবস ও ছুটি ঘোষণা সংক্রান্ত হাইকোর্টের রায় স্থগিত করেছেন সুপ্রিম কোর্টের আপিল বিভাগ। রাষ্ট্রপক্ষের আবেদনে হাইকোর্টের রায়ের বিরুদ্ধে আপিলের অনুমতি দিয়ে এই স্থগিতাদেশ দেয়া হয়েছে। সোমবার অতিরিক্ত অ্যাটর্নি জেনারেল ব্যারিস্টার অনিক আর হক এ তথ্য জানান। অতিরিক্ত অ্যাটর্নি জেনারেল ব্যারিস্টার অনিক আর হক জানান, রাষ্ট্রপক্ষের আবেদনে হাইকোর্টের রায়ের বিরুদ্ধে আপিলের অনুমতি দিয়ে এ স্থগিতাদেশ দেয়া হয়েছে বলে রাষ্ট্রপক্ষের আবেদনে হাইকোর্টের রায়ের বিরুদ্ধে রোববার আপিলের অনুমতি দিয়ে এই স্থগিতাদেশ দেয়া হয়েছে।১৯৯৬ সালে আওয়ামী লীগ ক্ষমতায় আসার পর বঙ্গবন্ধু শেখ মুজিবুর রহমানকে হত্যার দিনটি ১৫ই আগস্ট জাতীয় শোক দিবস এবং সরকারি ছুটি ঘোষণা করা হয়েছিল। তবে ২০০২ সালে বিএনপি’র নেতৃত্বে চারদলীয় জোট সরকারের মন্ত্রিসভার বৈঠকে জাতীয় শোক দিবস এবং সরকারি ছুটির সিদ্ধান্তটি বাতিল করা হয়। এর বিরুদ্ধে আওয়ামী লীগ-সমর্থিত তিনজন আইনজীবী রিট করলে হাইকোর্ট বিএনপি’র নেতৃত্বাধীন জোট সরকারের মন্ত্রিসভার সিদ্ধান্তকে অবৈধ ঘোষণা করে রায় দেন। সে রায়ের ফলে ১৫ই আগস্ট জাতীয় শোক দিবস পালন এবং সরকারি ছুটির সিদ্ধান্ত বহাল হয়। সমপ্রতি হাইকোর্টের ওই রায়ে স্থগিত চেয়ে আপিলের আবেদন করে রাষ্ট্রপক্ষ। রোববার আদালত আপিলের অনুমতি দিয়ে এই স্থগিতাদেশ দেয়া হলো।', '2024-12-03 00:00:00', '2024-12-03 03:51:06', '[]', 1813, '963ebb2da9fdddb1f8d85ce412d012c9');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746226, 2268, 'https://mzamin.com/news.php?news=138354', 'https://mzamin.com/news.php?news=138354', 'সংবাদ', NULL, 'সরকারি চাকরিতে শূন্যপদ ৫ লাখ নিয়োগ দিতে চিঠি', 'বর্তমানে সরকারি চাকরিতে শূন্য পদের সংখ্যা ৪ লাখ ৭৩ হাজার ১টি', 'বর্তমানে সরকারি চাকরিতে শূন্য পদের সংখ্যা ৪ লাখ ৭৩ হাজার ১টি। এসব পদ পূরণে সংশ্লিষ্ট মন্ত্রণালয়, বিভাগ এবং সংস্থাগুলোকে নির্দেশনা দিয়েছে জনপ্রশাসন মন্ত্রণালয়। সোমবার জনপ্রশাসন মন্ত্রণালয় থেকে এ সংক্রান্ত নির্দেশনার চিঠি সংশ্লিষ্টদের কাছে পাঠানো হয়েছে। এতে বলা হয়, সরকারি কর্মচারীদের পরিসংখ্যান ২০২৩ অনুযায়ী বর্তমানে সরকারের অনুমোদিত শূন্য পদের সংখ্যা ৪ লাখ ৭৩ হাজার ১টি। সমপ্রতি বাংলাদেশ পাবলিক সার্ভিস কমিশন (বিপিএসসি) ৪৭তম বিসিএস নিয়োগ পরীক্ষার মাধ্যমে ৩ হাজার ৬৮৮টি শূন্য পদ পূরণের জন্য বিজ্ঞপ্তি প্রকাশ করেছে। বিপিএসসি শুধু প্রথম শ্রেণি নন-ক্যাডার এবং সীমিত পর্যায়ে দ্বিতীয় শ্রেণির কর্মকর্তা নিয়োগ করে। সরকার থেকে অনুমোদিত অন্যান্য পদগুলো সংশ্লিষ্ট মন্ত্রণালয়/বিভাগ, অধীনস্ত দপ্তর/সংস্থা/করপোরেশন/কোম্পানি থেকে পূরণ করা হয়ে থাকে।\nচিঠিতে আরও বলা হয়, ছাত্র-জনতার গণ-অভ্যুত্থানের মাধ্যমে বর্তমান অন্তর্বর্তীকালীন সরকার জনগণের প্রত্যাশা পূরণ এবং শিক্ষিত তরুণদের বেকারত্ব লাঘবে নিরলসভাবে কাজ করে যাচ্ছে। দেশে বিপুল সংখ্যক শূন্যপদ থাকা সত্ত্বেও তা পূরণে কার্যকর পদক্ষেপ গ্রহণে কোনো উদ্যোগ পরিলক্ষিত হচ্ছে না। এ অবস্থায় মন্ত্রণালয়/বিভাগ, অধীন দপ্তর/সংস্থা এবং মাঠ পর্যায়ের অফিসগুলোতে অনুমোদিত শূন্য পদগুলো বিধি মোতাবেক পূরণের ব্যবস্থা নেয়ার নির্দেশ দেয়া হয় চিঠিতে।শূন্যপদ পূরণে এরইমধ্যে কি কি কার্যক্রম গ্রহণ করা হয়েছে, সেসব তথ্য ওয়ার্ড ফাইলসহ চিঠি পাওয়ার ১৫ কার্যদিবসের মধ্যে জনপ্রশাসন মন্ত্রণালয়ে পাঠানোর জন্য অনুরোধ জানিয়েছে জনপ্রশাসন মন্ত্রণালয়।', '2024-12-03 00:00:00', '2024-12-03 03:51:07', '[]', 1813, '3dbf92a63ce31222cab0f8766a575d31');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746227, 2268, 'https://mzamin.com/news.php?news=138353', 'https://mzamin.com/news.php?news=138353', 'সংবাদ', NULL, 'থার্ড টার্মিনালে অস্বাভাবিক ব্যয়ের তদন্ত হবে', 'অন্তর্বর্তীকালীন সরকারের বিমান ও পর্যটন উপদেষ্টা হাসান আরিফ বলেছেন, হযরত শাহজালাল আন্তর্জাতিক বিমানবন্দরের থার্ড টার্মিনাল নির্মাণে অস্বাভাবিক ব্যয় কেন হয়েছে তা তদন্ত করে দেখা হবে', 'অন্তর্বর্তীকালীন সরকারের বিমান ও পর্যটন উপদেষ্টা হাসান আরিফ বলেছেন, হযরত শাহজালাল আন্তর্জাতিক বিমানবন্দরের থার্ড টার্মিনাল নির্মাণে অস্বাভাবিক ব্যয় কেন হয়েছে তা তদন্ত করে দেখা হবে। এ ছাড়া গ্রাউন্ড হ্যান্ডেলিংয়ে বিমানের সক্ষমতার বিষয়টি আমরা দেখবো। দুই বছর যে আমরা হাত দিয়ে বসে থাকবো- বিষয়টা এমন নয়। যদি তার সক্ষমতার ঘাটতি হয় বিকল্প ব্যবস্থা নেয়া হবে। দুপুরে রাজধানীর একটি অভিজাত হোটেলে ‘শাহজালালের তৃতীয় টার্মিনাল: সম্ভাবনা ও চ্যালেঞ্জ’ বিষয়ে গোলটেবিল বৈঠকে তিনি এ কথা বলেন। গোলটেবিল বৈঠকের আয়োজন করে এভিয়েশন অ্যান্ড ট্যুরিজম জার্নালিস্টস ফোরাম অব বাংলাদেশ (এটিজেএফবি)। বৈঠকে সভাপতিত্ব করেন সংগঠনের সভাপতি তানজীম আনোয়ার। উপদেষ্টা বলেন, থার্ড টার্মিনাল নির্মাণে দুর্নীতি হয়েছে কিনা তা তদন্ত করা হবে। সেই সঙ্গে শ্বেতপত্র প্রকাশ করা হবে। আগামী এক বছরের মধ্যে থার্ড টার্মিনাল চালু হবে জানিয়ে তিনি বলেন, এখন থেকে ৩৬৫ দিন সময় লাগবে। তৃতীয় টার্মিনাল নির্মাণে যে বিনিয়োগ হয়েছে তা থেকে কেমন সেবা পাওয়া যাবে এবং দেশ ও জাতি লাভবান হবে- সেটা এখন বড় চ্যালেঞ্জ জানিয়ে হাসান আরিফ বলেন, তৃতীয় টার্মিনাল নির্মাণের পর প্রশ্ন আসছে ৩০ থেকে ৩২ হাজার কোটি টাকা ব্যয়, এই টার্মিনাল নির্মাণের পর আমাদের রিটার্ন কি? অর্থের দিক দিয়ে নয়, সার্ভিসের দিক দিয়ে রিটার্ন কি? থার্ড টার্মিনাল নির্মাণে ২৩ হাজার কোটি টাকা যে ব্যয় হয়েছে অবশ্যই দুর্নীতি হয়েছে বলে বেশি ব্যয় হয়েছে বিষয়টি আমরা তদন্ত করে দেখবো।গোলটেবিল বৈঠকে লিখিত প্রবন্ধ উপস্থাপন করেন এভিয়েশন অপারেটর’স এসোসিয়েশন অব বাংলাদেশের সেক্রেটারি ও নভোএয়ার লিমিটেডের ব্যবস্থাপনা পরিচালক মফিজুর রহমান। প্রবন্ধে মফিজুর রহমান বলেন, সমপ্রতি প্রধান উপদেষ্টার কার্যালয় থেকে বিমানের জিম্মায় তৃতীয় টার্মিনালের গ্রাউন্ড হ্যান্ডেলিং সেবা দুই বছরের জন্য প্রদানের নির্দেশনা দেয়া হয়েছে। বিষয়টি নিয়ে জোর বিতর্ক চলমান এবং তা যৌক্তিক। তৃতীয় টার্মিনাল অপারেশনের ক্ষেত্রে যেন রাজনীতির উপর অপারেশনাল বিষয় প্রাধান্য পায় তার জোর দাবি রাখবো। তিনি বলেন, তৃতীয় টার্মিনাল গ্রাউন্ড হ্যান্ডেলিং সেবা এবং টার্মিনাল অপারেশন উন্মুক্ত দরপত্রের মাধ্যমে বর্তমানের গ্রাউন্ড হ্যান্ডেলিং মনোপলি ভেঙে একাধিক কোম্পানিকে দেয়া হোক। এখানে বিমান প্রসঙ্গ নয়, গ্রাউন্ড হ্যান্ডেলিং সেবার মনোপলি ভাঙা সম্ভব হলে চূড়ান্ত বিচারে দেশ ও জাতি উপকৃত হবে। আর অন্য অপারেটরের সঙ্গে যোগ্য বিবেচনা হলে বিমানকেও জাতীয় প্রতিষ্ঠান হিসেবে গ্রাউন্ড হ্যান্ডেলিং সেবার দায়িত্ব দেয়া যেতে পারে। এতে দেশ এবং জনগণ একধারে আর্থিকভাবে লাভবান হবে এবং উন্নত সেবা পাবে।এভিয়েশন বিশেষজ্ঞ ও বিমান পরিচালনা বোর্ডের সাবেক সদস্য কাজী ওয়াহিদুল আলম বলেন, বিশ্বের কোথাও রাজধানীর ভেতর বিমানবন্দর নেই। সেখানে হযরত শাহজালাল আন্তর্জাতিক বিমানবন্দর তথা তৃতীয় টার্মিনাল নির্মাণের সময় তা বিবেচনা করা হয়নি। এমন অপরিকল্পিত কার্যক্রমের জন্য ৫৪ বছরেও বেসামরিক বিমান চলাচল কর্তৃপক্ষের (বেবিচক) অর্জন শূন্য। তিনি বলেন, বিশ্বের কোথাও কোনো একটি সংস্থা একক ভাবে বিমানবন্দর গ্রাউন্ড হ্যান্ডেলিং করে না। সেখানে শাহজালাল বিমানবন্দরসহ দেশের সব বিমানবন্দর এককভাবে গ্রাউন্ড হ্যান্ডেলিং করছে বিমান। এ বিষয়ে সেবা দিতে গিয়ে বিমানের বিরুদ্ধে অভিযোগের অন্ত নেই। এখন তারা শাহজালাল বিমানবন্দরের তৃতীয় টার্মিনালের গ্রাউন্ড হ্যান্ডেলিং নেয়ার জন্য চেষ্টা করছে। এটা তারা সঠিকভাবে পরিচালনা করতে পারবে তা বলা যায় না।বেসামরিক বিমান চলাচল কর্তৃপক্ষের চেয়ারম্যান মনজুর কবির ভূঁইয়া বলেন, থার্ড টার্মিনালসহ দেশের সকল টার্মিনালগুলোকে উন্নত করার জন্য কাজ করছি। এক্সপোর্ট এবং ইমপোর্ট নিয়ে একটি টার্মিনাল হচ্ছে বলেও জানান তিনি। এই টার্মিনালের যাবতীয় সুযোগ-সুবিধা বৃদ্ধির জন্য উপদেষ্টার সঙ্গে তাদের কথা হয়েছে। তৃতীয় টার্মিনাল চালু জাতির কাছে স্বপ্ন। তবে এজন্য তারা বসে নেই। নিরলসভাবে কাজ করে যাচ্ছেন। কর্তৃপক্ষ যেটা ভালো হয় সেটাই করার চেষ্টা করছেন বলে জানান তিনি। এয়ার এ্যাস্ট্রা’র সিইও ইমরান আসিফ বলেন, একটি ভবন থাকলে শুধু হবে না। আমাদের মাল্টিপল গ্রাউন্ড সার্ভিস প্রভাইডার লাগবে। এর পাশাপাশি আমাদের দক্ষ ভালো ম্যানেজমেন্ট ও এয়ার ট্রাফিক কন্ট্রোল করতে পারে- এমন জনবলও লাগবে। আমাদের টার্মিনালগুলোতে যেসব লোক কাজ করে তাদের বেতন পর্যাপ্ত নয়। যারা গ্রাউন্ড হ্যান্ডেলিং সেবা দেবেন তাদের সেবায় যেনো দেশি-বিদেশি এয়ারলাইন্সগুলোর যাত্রীরা সন্তুষ্ট থাকে। এসোসিয়েশন অব ট্রাভেল এজেন্টস অব বাংলাদেশ (আটাব) সভাপতি আবদুস সালাম আরেফ বলেন, বাংলাদেশের জাতীয় পরিবহন করে বিদেশে এয়ারলাইন্সগুলো সন্তুষ্ট না। তারা জানিয়েছে, বিশ্ব অন্যান্য যেকোনো দেশের তুলনায় শাহজালাল বিমানবন্দরে গ্রাউন্ড হ্যান্ডেলিং চার্জ বেশি। তাদেরকে সন্তুষ্ট করতে হলে শাহজালাল বিমানবন্দরকে এভিয়েশন হাব করতে হবে।\nএয়ারলাইন্স অপারেটরস কমিটির ঢাকার (এওসি) চেয়ারপারসন দিলারা হোসেন বলেন, শাহজালাল বিমানবন্দরে আজ থেকে ৩০-৪০ বছর আগে যেসব অভিযোগ শুনেছি- এখনো একই ধরনের অভিযোগ আসছে। এই বিমানবন্দরে ক্রমেই বিদেশি এয়ারলাইন্স বাড়ছে। কিন্তু গ্রাউন্ড হ্যান্ডেলিংয়ের সেবার মান বাড়ছে না।', '2024-12-03 00:00:00', '2024-12-03 03:51:08', '[\"https://mzamin.com/uploads/news/main/138353_11.webp\"]', 1813, 'cfe80d89975683bb9f5000c61600be68');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746228, 2268, 'https://mzamin.com/news.php?news=138350', 'https://mzamin.com/news.php?news=138350', 'সংবাদ', NULL, 'শ্রীনগরে অপহরণের পর প্রবাসীকে হত্যা', 'শ্রীনগরে টাকার লোভে অপহরণের পর সিঙ্গাপুর প্রবাসী রমজান মুন্সীকে গলায় রশি পেঁচিয়ে হত্যা করা হয়', 'শ্রীনগরে টাকার লোভে অপহরণের পর সিঙ্গাপুর প্রবাসী রমজান মুন্সীকে গলায় রশি পেঁচিয়ে হত্যা করা হয়। হত্যার পর লাশ নৌকায় উঠিয়ে গুম করতে নেয়ার সময় নৌকা ডুবে যায়। দু’দিন পর লাশটি খালে ভেসে ওঠে। ভাসমান লাশ উদ্ধারের ৪ দিন পর তিন জনকে গ্রেপ্তার করে পুলিশ। তাদের মধ্যে অপহরণ ও হত্যার মূল পরিকল্পনাকারী ওয়াসিম খান (৩৬) রোববার মুন্সীগঞ্জ আদালতে ১৬৪ ধারায় স্বীকারোক্তিমূলক জবানবন্দি প্রদান করেছে। বাকি আসামি শাহিন প্রধান (২২) ও রনিকে (৩৫) গতকাল দুপুরে মুন্সীগঞ্জ আদালতে প্রেরণ করা হয়েছে।পুলিশ জানায়, শ্রীনগর উপজেলার গাদিঘাট গ্রামের মাজেরহাটির সিরাজুল ইসলাম খোকা মুন্সীর ছেলে রমজান মুন্সী (৩৯) অনেকদিন ধরে সিঙ্গাপুর থাকেন। ২ মাস আগে তিনি দেশে এসে দেউলভোগ ব্রিজের পাশে ক্রয়কৃত জায়গায় বহুতল ভবন নির্মাণের প্রস্তুতি শুরু করেন। এ সময় তার প্লটের পাশের মালিক মুক্তার মাঝির ছেলে ইভান জায়গা দাবি করে লোকজন নিয়ে কয়েকবার বাধা দেয়। রমজানের পূর্বপরিচিত দেউলভোগ এলাকার কানু খানের ছেলে ওয়াসিমসহ কয়েকজন মিলে বিষয়টি ১০ লাখ টাকায় মীমাংসা করে দেয়। রমজান মুন্সী ১০ লাখ টাকা ইভানকে দিলে সে না দাবি দলিল করে দেবে বলে সিদ্ধান্ত হয়। গত ২৪ নভেম্বর রাত ১০টায় ওয়াসিম ফোন করে পরদিন সকালে রমজান মুন্সীকে জমি রেজিস্ট্রি করে দেওয়া হবে বলে দেউলভোগ আসতে বলে।ওয়াসিমের নানাবাড়ি রমজান মুন্সীর পাশের বাড়ি হওয়ায় ছোটবেলা থেকেই তাদের সখ্য ছিল। এ কারণে ওয়াসিমের ওপর ভরসা করে রমজান ২৫ নভেম্বর বেলা ১১টায় দেউলভোগ মোড়ে ওয়াসিমদের বিল্ডিংয়ের সামনে আসে। এ সময় ওয়াসিম তার মালিকানাধীন দেউলভোগ বাজারে পরিত্যাক্ত বিএসডিসি বীজাগারের অটোগ্যারেজে নিয়ে যায়। সেখানে গিয়ে রমজানের কাছে টাকা দাবি করে। রমজান জমি লিখে দিতে রেজিস্ট্রি অফিসে যেতে বলে। এতে ওয়াসিম ক্ষিপ্ত হয়ে তার গ্যারেজের ম্যানেজার শাহিন, অটোচালক রনি ও রিয়াজসহ রমজান মুন্সীকে একটি অটোরিকশার ভিতরে বেঁধে ফেলে। এক পর্যায়ে তার মুখে কসটেপ লাগিয়ে বেধড়ক পেটানো হয়। এ সময় রমজান গোঙানির মতো শব্দ করতে থাকলে ওয়াসিম তার গলায় রশি পেঁচিয়ে গ্যারেজ থেকে বের হয়ে আসে। গ্যারেজের অন্য অটোচালকদের থেকে আড়াল করতে রমজানকে রাখা অটোর সামনে বেশ কয়েকটি অটোরিকশা রেখে দেওয়া হয়। ৩টার দিকে রমজান মারা যায়। রাতে ওয়াসিম, শাহিন, রনি, রিয়াদ মিলে রমজানের জামাকাপড় খুলে লাশ বস্তায় ভরে পাশের খালের একটি নৌকায় ওঠায়। লাশটি গুম করার জন্য নৌকা নিয়ে রওনা হলে উপজেলা পরিষদের পাশের ব্রিজের কাছে এসে তা ডুবে যায়। এ সময় লাশ রেখেই তারা তীরে উঠে এসে যে যার মতো চলে যায়।\nমামলার তদন্তকারী অফিসার শ্রীনগর থানার এসআই হামিম মো. তারেক অভিযান চালিয়ে ঢাকার কদমতলী এলাকা থেকে ওয়াসিমকে গ্রেপ্তার করে। তার দেয়া তথ্যের ভিত্তিতে উপজেলার হরপাড়া থেকে শাহিন ও দেউলভোগ এলাকা থেকে রনিকে গ্রেপ্তার করা হয়। শাহিনের বাড়ি যশোরের কোট চাঁদপুরে। সে স্ত্রীকে নিয়ে হরপাড়া এলাকায় ভাড়া থাকত। অপরদিকে রনির স্থায়ী কোনো ঠিকানা নেই। সে দেউলভোগ আমির এলাকায় বিয়ে করে সেখানেই ভাড়া বাড়িতে থাকত। হত্যাকাণ্ডে অংশ নেওয়া রিয়াজকে গ্রেপ্তারের চেষ্টা চলছে। শ্রীনগর থানার ওসি মো. কাইয়ূম উদ্দিন চৌধুরী বলেন, দ্রুততম সময়ে ক্লুলেস রমজান মুন্সী হত্যাকাণ্ডের সঙ্গে জড়িত তিনজনকে গ্রেপ্তার করে রহস্য উদ্ঘাটন করা হয়েছে। আসামিদের আদালতে প্রেরণ করা হয়েছে।', '2024-12-03 00:00:00', '2024-12-03 03:51:08', '[\"https://mzamin.com/uploads/news/main/138350_11.webp\"]', 1813, '67eedfb80a3d005f22873e80f23612d2');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746229, 2268, 'https://mzamin.com/news.php?news=138349', 'https://mzamin.com/news.php?news=138349', 'সংবাদ', NULL, 'পার্বত্য শান্তিচুক্তিতে সংবিধানের সঙ্গে সাংঘর্ষিক ধারা সংশোধনের দাবি', 'পার্বত্য চট্টগ্রাম চুক্তি তথা শান্তিচুক্তির ২৭ বছর পূর্তিতে বাংলাদেশের সংবিধানের সঙ্গে সাংঘর্ষিক ও বৈষম্যমূলক ধারাগুলো সংশোধন করে চুক্তির পুনর্মূল্যায়নের দাবিতে সংবাদ সম্মেলন করেছে পার্বত্য চট্টগ্রাম নাগরিক পরিষদ', 'পার্বত্য চট্টগ্রাম চুক্তি তথা শান্তিচুক্তির ২৭ বছর পূর্তিতে বাংলাদেশের সংবিধানের সঙ্গে সাংঘর্ষিক ও বৈষম্যমূলক ধারাগুলো সংশোধন করে চুক্তির পুনর্মূল্যায়নের দাবিতে সংবাদ সম্মেলন করেছে পার্বত্য চট্টগ্রাম নাগরিক পরিষদ\nপার্বত্য চট্টগ্রাম চুক্তি তথা শান্তিচুক্তির ২৭ বছর পূর্তিতে বাংলাদেশের সংবিধানের সঙ্গে সাংঘর্ষিক ও বৈষম্যমূলক ধারাগুলো সংশোধন করে চুক্তির পুনর্মূল্যায়নের দাবিতে সংবাদ সম্মেলন করেছে পার্বত্য চট্টগ্রাম নাগরিক পরিষদ। গতকাল সকাল সাড়ে ১১টায় খাগড়াছড়িস্থ এফএনএফ রেস্টুরেন্টে সংবাদ সম্মেলন করে এ দাবি জানান পার্বত্য চট্টগ্রাম নাগরিক পরিষদের জেলা ও কেন্দ্রীয় নেতারা। সংবাদ সম্মেলনে লিখিত বক্তব্য পাঠ করেন সংগঠনটির জেলা সভাপতি ইঞ্জিনিয়ার লোকমান হোসেন। এ সময় কেন্দ্রীয় সাংগঠনিক সম্পাদক আনিসুজ্জামান ডালিম, মহিলা পরিষদের কেন্দ্রীয় সভাপতি সালমা আক্তার মৌ, জেলা কমিটির সহ-সভাপতি নিজাম উদ্দিন, সাধারণ সম্পাদক এসএম মাসুম রানা, সাংগঠনিক সম্পাদক মোক্তাদের হোসেন, যুব পরিষদের কেন্দ্রীয় সভাপতি মো. আসাদ উল্লাহ প্রমুখ উপস্থিত ছিলেন। লোকমান হোসেন বলেন, প্রায় দুই দশকেরও বেশি সময় পার্বত্য চট্টগ্রামে তৎকালীন শান্তিবাহিনী রক্তের হোলি খেলায় মেতে ছিল। ১৯৯৭ সালের ২ ডিসেম্বর তৎকালীন আওয়ামী লীগ সরকার ও জনসংহতি সমিতির (জেএসএস) মধ্যে পার্বত্য চুক্তি হয়। এ চুক্তির অধিকাংশ ধারা ছিল বাংলাদেশের সংবিধানের পরিপন্থি। চুক্তিটি ছিল রাষ্ট্রীয় অখণ্ডতা, কর্তৃত্ব ও এখতিয়ারবিরোধী। চুক্তি বাস্তবায়ন করতে গিয়ে ইতিমধ্যে পাহাড় থেকে একটি পূর্ণ সেনা ব্রিগেড এবং ২৩৮টি নিরাপত্তাবাহিনীর ক্যাম্প প্রত্যাহার করা হয়েছে। যার ফলে প্রতিনিয়ত পাহাড়ে চলছে সন্ত্রাসীদের অস্ত্রের মহড়া। মহোৎসবে চলছে চাঁদাবাজি, সন্ত্রাসী কর্মকাণ্ড। বছরে প্রায় ৫০০ কোটি টাকা চাঁদাবাজি করছে পাহাড়ি ৫টি সশস্ত্র সন্ত্রাসী গ্রুপ। তিনি বলেন, ইতিমধ্যে পার্বত্য চুক্তির বিভিন্ন ধারা চ্যালেঞ্জ করে উচ্চ আদালতে রিট করা হয়েছে। এই রিটের রায়ে উচ্চ আদালত পার্বত্য চুক্তির অনেকগুলো ধারা অসাংবিধানিক বলে বাতিল করে দিয়েছে। যদিও উচ্চ আদালতের এই রায়ের বিরুদ্ধে স্থগিতাদেশ দেওয়া হয়েছে। মামলাটি আপিল বিভাগে বিচারাধীন রয়েছে। বর্তমান অন্তর্বর্তীকালীন সরকারের প্রধান উপদেষ্টার কাছে পার্বত্য চট্টগ্রাম চুক্তির সঙ্গে বাংলাদেশের সংবিধানের সাংঘর্ষিক ও বৈষম্যমূলক ধারাগুলো সংশোধনপূর্বক চুক্তির পুনর্মূল্যায়ন করার দাবি জানান। পাশাপাশি পার্বত্য চট্টগ্রামে শান্তি স্থাপনে ব্যর্থ আঞ্চলিক পরিষদের চেয়ারম্যান সন্তু লারমার অপসারণ, পাহাড়ের অবৈধ অস্ত্র উদ্ধার ও স্থায়ী শান্তি প্রতিষ্ঠার জন্য প্রত্যাহারকৃত নিরাপত্তাবাহিনীর ক্যাম্প পুনঃস্থাপনের জোর দাবি জানান নেতারা।', '2024-12-03 00:00:00', '2024-12-03 03:51:09', '[]', 1813, '85602d20e438282dd5e192bee898f456');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746230, 2268, 'https://mzamin.com/news.php?news=138348', 'https://mzamin.com/news.php?news=138348', 'সংবাদ', NULL, 'মৌলভীবাজারে ধর্মীয় ও রাজনৈতিক নেতাদের সঙ্গে পুলিশ সুপারের মতবিনিময়', 'মিথ্যা তথ্যের বিরুদ্ধে সত্য প্রচারই বড় প্রতিবাদ বলে মন্তব্য করেছেন মৌলভীবাজারের পুলিশ সুপার এমকেএইচ, জাহাঙ্গীর হোসেন পিপিএম', 'মিথ্যা তথ্যের বিরুদ্ধে সত্য প্রচারই বড় প্রতিবাদ বলে মন্তব্য করেছেন মৌলভীবাজারের পুলিশ সুপার এমকেএইচ, জাহাঙ্গীর হোসেন পিপিএম\nমিথ্যা তথ্যের বিরুদ্ধে সত্য প্রচারই বড় প্রতিবাদ বলে মন্তব্য করেছেন মৌলভীবাজারের পুলিশ সুপার এমকেএইচ, জাহাঙ্গীর হোসেন পিপিএম। সাম্প্রতিক সময়ে সামাজিক যোগাযোগ মাধ্যমে প্রচারিত গুজবের বিরুদ্ধ সঠিক তথ্য প্রচারের মাধ্যমে প্রতিবাদ করার আহ্বান জানান। গতকাল দুপুরে মৌলভীবাজার জেলা পুলিশ আয়োজিত মতবিনিময় সভায় এসব এই আহ্বান জানান। সামপ্রতিক সময়ে ধর্মীয় বিষয়কে কেন্দ্র করে ফেসবুকসহ বিভিন্ন সামাজিক যোগাযোগ মাধ্যমে মিথ্যা তথ্য ও গুজব প্রতিরোধে মৌলভীবাজার জেলার বিভিন্ন ধর্মীয় ও রাজনৈতিক দলের নেতাদের সঙ্গে পুলিশ সুপারের মতবিনিময় সভা অনুষ্ঠিত হয়। মৌলভীবাজার পুলিশ সুপারের কার্যালয়ের সম্মেলন কক্ষে এই মতবিনিময় সভার আয়োজন করা হয়। জেলা পুলিশ আয়োজিত এই মতবিনিময় সভায় মৌলভীবাজার জেলার হিন্দু, মুসলিম এবং বিভিন্ন রাজনৈতিক দলের নেতা অংশগ্রহণ করেন। মতবিনিময় সভায় ছিলেন অতিরিক্ত পুলিশ সুপার সুদর্শন কুমার রায়, অতিরিক্ত পুলিশ সুপার মোহসিন, অতিরিক্ত পুলিশ সুপার মো. আজমল হোসেন, মৌলভীবাজার সদর থানার অফিসার ইনচার্জ গাজী মাহবুবুর রহমানসহ মৌলভীবাজার জেলা বিএনপি, জামায়াতে ইসলামী, হেফাজতে ইসলাম, হিন্দু-বৌদ্ধ-খ্রিষ্টান কল্যাণ ফ্রন্ট, খেলাফত মজলিসসহ অন্য রাজনৈতিক দলের নেতারা।', '2024-12-03 00:00:00', '2024-12-03 03:51:09', '[\"https://mzamin.com/uploads/news/main/138348_11.webp\"]', 1813, 'dd0a470fb45c825e6e3c923b470a00b8');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746231, 2268, 'https://mzamin.com/news.php?news=138347', 'https://mzamin.com/news.php?news=138347', 'সংবাদ', NULL, 'শেখ হাসিনা মানুষের কথা বলার স্বাধীনতা কেড়ে নিয়েছিল- ডা. শফিকুর রহমান', 'জামায়াতের আমীর ডা. শফিকুর রহমান বলেছেন, শেখ হাসিনার সরকারের আমলে সারা বাংলাদেশকে একটি কারাগারে পরিণত করা হয়েছিল', 'জামায়াতের আমীর ডা. শফিকুর রহমান বলেছেন, শেখ হাসিনার সরকারের আমলে সারা বাংলাদেশকে একটি কারাগারে পরিণত করা হয়েছিল\nজামায়াতের আমীর ডা. শফিকুর রহমান বলেছেন, শেখ হাসিনার সরকারের আমলে সারা বাংলাদেশকে একটি কারাগারে পরিণত করা হয়েছিল। কথা বলার স্বাধীনতা কেড়ে নেয়া হয়েছিল। রোববার রাতে বরিশাল সফর উপলক্ষে নগরীর নথুল্লাবাদ কেন্দ্রীয় বাস টার্মিনালে অনুষ্ঠিত পথসভায় প্রধান অতিথির বক্তব্যে তিনি এ কথা বলেন।জামায়াতের আমীর বলেন, আওয়ামী লীগ চেয়েছিল তারা ২০৪১ সাল পর্যন্ত ক্ষমতায় থাকবে। এমন একটা ভাব তারা দেশের মালিক আর আমরা সবাই ভাড়াটে। কিন্তু আমরা এমন একটা বাংলাদেশ চাই যেখানে কোনো গুম, খুন, ছিনতাই হবে না। সবাই সম্মানের সঙ্গে নিজ মর্যাদা নিয়ে চলবে। আমরা এমন যুবসমাজ চাই, যেখানে মাদকের ছোঁয়া থাকবে না তিন মাস আগেও কল্পনা করতে পারিনি খোলা ময়দানে মানুষের সামনে দাঁড়িয়ে দোয়া চাইতে পারব। ২০০৬ সালের ২৮ই আগস্ট থেকে শুরু করে ২০২৪ সালের ৫ই আগস্ট পর্যন্ত বহু মানুষকে গুম ও খুন করা হয়েছে। বহু মানুষকে নির্মমভাবে অত্যাচার করা হয়েছিল শুধু সত্যের পক্ষে থাকার জন্য। প্রবীণ ও বিজ্ঞ আলেমদেরও তারা জেলে দিতে দ্বিধাবোধ করেনি। তাদের পায়েও ডান্ডা বেঁড়ি পরিয়েছে। কোমরে দড়ি পরিয়েছে। আমি বিশ্বাস করি আল্লাহর আরশ কেঁপে উঠেছিল এই নির্দোষ আলেমদের জন্য। কোরআনের পাখি আল্লামা দেলাওয়ার হোসাইন সাঈদীকে তারা তিলে তিলে খুন করেছে। আমাদের সিপাহীসালার বহু নেতাকে তিলে তিলে খুন করা হয়েছে। তিনি বলেন, আমাদের তিনজন শীর্ষস্থানীয় নেতা একাধারে মন্ত্রণালয়ের দায়িত্বে ছিল। আমরা গর্বের সঙ্গে বলতে পারি তাদের হাত ছিল দুর্নীতিমুক্ত। সেই দলের মানুষই আজ জামায়াতে ইসলামী করেন। জামায়াত ক্ষমতায় গেলে মালিক নয়, সেবক হয়ে থাকবে জানিয়ে বলেন, আমার যে সম্পদ আছে তা বাড়বে না। প্রয়োজনে জনগণের খেদমতে সেই সম্পদ বিলিয়ে দেব। মহানগর আমির অধ্যক্ষ জহির উদ্দিন মোহাম্মদ বাবরের সভাপতিত্বে ও মহানগর সেক্রেটারি মাওলানা মতিউর রহমানের সঞ্চালনায় প্রোগ্রামে বিশেষ অতিথি ছিলেন বাংলাদেশ জামায়াতে ইসলামীর কেন্দ্রীয় নির্বাহী পরিষদ সদস্য মোবারক হোসাইন, বরিশাল জেলা আমির অধ্যাপক আব্দুল জব্বার, ভোলা জেলা আমির জাকির হোসাইন, পটুয়াখালী জেলা আমির এডভোকেট নামজুল আহসান, ঝালকাঠি জেলা আমির এডভোকেট হাফিজুর রহমান। আরও উপস্থিত ছিলেন মহানগরীর নায়েবে আমির হোসাইন ইবনে আহমেদ এসিস্ট্যান্ট সেক্রেটারি মাস্টার মিজানুর রহমান, হাফেজ হাসান আতিক, তারিকুল ইসলাম কর্মপরিষদ সদস্য অধ্যক্ষ আমিনুল ইসলাম খসরু, মাওলানা জয়নুল আবেদিন, মাওলানা শহিদুল ইসলাম, মাওলানা শফিউল্লাহ তালুকদার, অধ্যাপক মাহফুজুর রহমান আমিন, মুহাম্মদ জাফর ইকবাল, শামীম কবির, মোস্তাফিজুর রহমান, অধ্যাপক আনোয়ার হোসাইন, অধ্যাপক সুলতানুল আরেফিন, মুজিবুর রহমান, মোয়াজ্জেম হোসেন হাওলাদার, এডভোকেট আবুল খায়ের শহীদ প্রমুখ।', '2024-12-03 00:00:00', '2024-12-03 03:51:10', '[]', 1813, '43567bce14de6bde0dea78fc217bfa24');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746232, 2268, 'https://mzamin.com/news.php?news=138346', 'https://mzamin.com/news.php?news=138346', 'সংবাদ', NULL, 'কিশোরগঞ্জে এডিসি’র গাড়ির ধাক্কায় মুয়াজ্জিন নিহত', 'নীলফামারীর কিশোরগঞ্জে এডিসি’র গাড়ির ধাক্কায় মোটরসাইকেল আরোহী মুয়াজ্জিন নাজমুল ইসলাম (৩৫) নিহত হয়েছেন', 'নীলফামারীর কিশোরগঞ্জে এডিসি’র গাড়ির ধাক্কায় মোটরসাইকেল আরোহী মুয়াজ্জিন নাজমুল ইসলাম (৩৫) নিহত হয়েছেন\nনীলফামারীর কিশোরগঞ্জে এডিসি’র গাড়ির ধাক্কায় মোটরসাইকেল আরোহী মুয়াজ্জিন নাজমুল ইসলাম (৩৫) নিহত হয়েছেন। সোমবার সকালে রংপুর-ডালিয়া সড়কের বড়ভিটা পেট্রলপাম্প সংলগ্ন স্থানে এ দুর্ঘটনা ঘটে। নিহত নাজমুল উত্তর বড়ভিটা দলবাড়ী গ্রামের মৃত জমসের আলীর ছেলে ও বাজেডুমরিয়া পূর্বপাড়া জামে মসজিদের মুয়াজ্জিম ছিলেন।জানা গেছে, ওই মুয়াজ্জিন পেট্রোল নেয়ার জন্য মোটরসাইকেলে রাস্তা পার হয়ে পাম্পের দিকে যাচ্ছিলেন। এ সময় পঞ্চগড় এডিসি’র (শিক্ষা) জিপগাড়ির সঙ্গে মুখোমুখি সংঘর্ষে ঘটনাস্থলেই তিনি নিহত হন। বিষয়টি নিশ্চিত করে কিশোরগঞ্জ থানার ওসি আশরাফুল ইসলাম মরদেহ তার পরিবারের কাছে হস্তান্তর করা হয়েছে বলে জানান।', '2024-12-03 00:00:00', '2024-12-03 03:51:10', '[]', 1813, '576be4987c95f1c878795e2381fec281');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746233, 2268, 'https://mzamin.com/news.php?news=138345', 'https://mzamin.com/news.php?news=138345', 'সংবাদ', NULL, 'পরশুরাম সীমান্তে ভারতীয়দের স্লোগান  বিজিবি’র প্রতিবাদ', 'ফেনীর পরশুরামের বিলোনিয়া স্থলবন্দরের ভারত অংশ থেকে বাংলাদেশকে কটাক্ষ করে বিভিন্ন স্লোগান দিয়েছে সনাতনী হিন্দু সমাজের ব্যানারে কিছু মানুষ', 'ফেনীর পরশুরামের বিলোনিয়া স্থলবন্দরের ভারত অংশ থেকে বাংলাদেশকে কটাক্ষ করে বিভিন্ন স্লোগান দিয়েছে সনাতনী হিন্দু সমাজের ব্যানারে কিছু মানুষ\nফেনীর পরশুরামের বিলোনিয়া স্থলবন্দরের ভারত অংশ থেকে বাংলাদেশকে কটাক্ষ করে বিভিন্ন স্লোগান দিয়েছে সনাতনী হিন্দু সমাজের ব্যানারে কিছু মানুষ। এ বিষয়ে বিএসএফ’র কাছে প্রতিবাদ জানিয়েছে বিজিবি। প্রত্যক্ষদর্শীরা জানান, রোববার সন্ধ্যায় বিলোনিয়া স্থলবন্দরের ওপারে ভারতীয় ৫০-৬০ জন নাগরিক জড়ো হয়ে বাংলাদেশের বিরুদ্ধে বিভিন্ন স্ল্লোগান দিতে থাকে। এ সময় ভারতের নো-ম্যান্সল্যান্ড অংশে ঢুকে পড়ে বাংলাদেশের দিকে মাইক তাক করে উচ্চশব্দে প্রায় ৩০ মিনিট ধরে এসব স্লোগান দেয়। পরে বিএসএফ তাদের সরিয়ে দেয়। এদিকে সোমবার বিলোনিয়া স্থলবন্দরে রপ্তানি কার্যক্রম স্বাভাবিক রয়েছে। বিলোনিয়া কাস্টমসের এসআই আমিনুল হক বিষয়টি নিশ্চিত করেছেন। বিজিবি’র মজুমদারহাট সদরের কোম্পানি কমান্ডার সৈয়দ কামরুল আলম মজুমদার বলেন, বিজিবি’র সদস্যরা বিলোনিয়া স্থলবন্দরে অবস্থান নিয়ে বিএসএফ’র কোম্পানি কমান্ডারকে ডেকে প্রতিবাদ জানানো হয়েছে। বিজিবি’র ফেনী ব্যাটালিয়নের অধিনায়ক লে. কর্নেল মোশারফ হোসেন বলেন, বিজিবি’র পক্ষ থেকে লিখিতভাবে বিএসএফকে প্রতিবাদ পাঠানোর প্রক্রিয়া চলছে।', '2024-12-03 00:00:00', '2024-12-03 03:51:11', '[]', 1813, 'f6ec73f0734c9be4920ad7c05a9ee6d2');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746234, 2268, 'https://mzamin.com/news.php?news=138344', 'https://mzamin.com/news.php?news=138344', 'সংবাদ', NULL, 'সিরাজগঞ্জে বেহাল সড়কগুলো সংস্কারের দাবিতে মানববন্ধন', 'জনদুর্ভোগরোধে ও জনস্বার্থে সিরাজগঞ্জ শহরের জনগুরুত্বপূর্ণ সড়কগুলোর নির্মাণকাজ দ্রুত সমাপ্ত ও খানাখন্দে ভরা সংস্কারহীন সিরাজগঞ্জ কাঠেরপুল থেকে কাজিপুরের রাস্তার সংস্কার ও মেরামতের দাবিতে মানববন্ধন অনুষ্ঠিত হয়েছে', 'জনদুর্ভোগরোধে ও জনস্বার্থে সিরাজগঞ্জ শহরের জনগুরুত্বপূর্ণ সড়কগুলোর নির্মাণকাজ দ্রুত সমাপ্ত ও খানাখন্দে ভরা সংস্কারহীন সিরাজগঞ্জ কাঠেরপুল থেকে কাজিপুরের রাস্তার সংস্কার ও মেরামতের দাবিতে মানববন্ধন অনুষ্ঠিত হয়েছে\nজনদুর্ভোগরোধে ও জনস্বার্থে সিরাজগঞ্জ শহরের জনগুরুত্বপূর্ণ সড়কগুলোর নির্মাণকাজ দ্রুত সমাপ্ত ও খানাখন্দে ভরা সংস্কারহীন সিরাজগঞ্জ কাঠেরপুল থেকে কাজিপুরের রাস্তার সংস্কার ও মেরামতের দাবিতে মানববন্ধন অনুষ্ঠিত হয়েছে। গতকাল সকাল ১১টায় সিরাজগঞ্জ শহরের কাঠেরপুলে ‘আমার সিরাজগঞ্জবাসী’র ব্যানারে আয়োজিত মানববন্ধনে প্রধান অতিথি হিসেবে বক্তব্য রাখেন ‘আমরা সিরাজগঞ্জবাসী’র সমন্বয়ক, বিএনপি জাতীয় নির্বাহী কমিটির সদস্য ও সিরাজগঞ্জ জেলা বিএনপি’র সাধারণ সম্পাদক সাইদুর রহমান বাচ্চু। এ সময় আরও বক্তব্য রাখেন সিরাজগঞ্জ জেলা বিএনপি’র তথ্য ও গবেষণা সম্পাদক ও মিডিয়া সেলের সদস্য সচিব দুলাল উদ্দীন আহমেদ, সহ-প্রচার সম্পাদক রেজাউল করিম খান, জেলা ছাত্রদলের সভাপতি জুনায়েত হোসেন সবুজ প্রমুখ।এ সময় ‘আমরা সিরাজগঞ্জবাসী’র সমন্বয়ক জেলা বিএনপি’র সাধারণ সম্পাদক সাইদুর রহমান বাচ্চু বলেন, ঐতিহ্যবাহী সিরাজগঞ্জ শহরের ভেতরের জনগুরুত্বপূর্ণ সড়কগুলোর নির্মাণকাজে ধীরগতি হওয়ায় জনদুর্ভোগ চরম পর্যায়ে পৌঁছে গেছে। বিশেষ করে সিরাজগঞ্জ সড়ক ও জনপথ বিভাগের নিয়ন্ত্রণাধীন সিরাজগঞ্জ বাজার স্টেশন নতুন সংযোগ সড়ক ও সিরাজগঞ্জ-কাজিপুর সড়ক প্রশস্তকরণ কাজে ধীরগতি হওয়ায় জনদুর্ভোগ বৃদ্ধি পেয়েছে। শুধু তাই নয়, সংশ্লিষ্ট কর্তৃপক্ষ ও ঠিকাদারী প্রতিষ্ঠানের গাফিলতি ও উদাসীনতার কারণে প্রতিনিয়ত ঘটছে ছোট-বড় নানা দুর্ঘটনা।সাইদুর রহমান বাচ্চু আরও বলেন, সিরাজগঞ্জ পৌর শহরের গোশালা সড়ক, থানা রোড-নয়ন মোড় সড়ক, ভাসানী রোড-বনানজী শালসা সড়ক ও জেলাখানা অভিমুখী শহীদ এম মনসুর আলী সড়কে ব্যাপক খানাখন্দে ভরে গেছে। এতে পৌরসভার নাগরিকদের চলাচলের ক্ষেত্রে নানা ভোগান্তি পোহাতে হচ্ছে। নির্মাণাধীন এই সড়কগুলো ঘিরে উন্নয়নের নব দিগন্তের স্বপ্ন দেখছে সিরাজগঞ্জবাসী। সড়কগুলোর কারণে অন্যান্য সব উপজেলা ও আশপাশের জেলাগুলোর সঙ্গে সিরাজগঞ্জের যেগাযোগ ব্যবস্থারও আমুল পরিবর্তনের স্বপ্ন দেখছেন সিরাজগঞ্জ শহরবাসী। অথচ পৌর কর্তৃপক্ষের এ ব্যাপারে দৃশ্যমান কোনো তৎপরতা নেই। অনতিবিলম্বে সিরাজগঞ্জ পৌরবাসীসহ শহরমুখী সর্বসাধারণের যাতায়াতের সুবিধার্থে ও জনদুর্ভোগ লাঘবে নির্মাণাধীন সড়কগুলো কাজ দ্রুত বাস্তবায়ন ও সংস্কারহীন সড়কগুলো দ্রুত সময়ের মধ্যে মেরামতের যাবতীয় ব্যবস্থা গ্রহণের দাবি জানিয়েছেন। মানববন্ধন শেষে সিরাজগঞ্জ জেলা প্রশাসকের কার্যালয়ের অতিরিক্ত জেলা প্রশাসক গণপতি রায়ের কাছে স্মারকলিপি প্রদান করেন জেলা বিএনপি’র সাধারণ সম্পাদক সাইদুর রহমান বাচ্চু।', '2024-12-03 00:00:00', '2024-12-03 03:51:12', '[\"https://mzamin.com/uploads/news/main/138344_11.webp\"]', 1813, 'da28ed95af5b59ef149ac2e035b166d3');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746235, 2268, 'https://mzamin.com/news.php?news=138343', 'https://mzamin.com/news.php?news=138343', 'সংবাদ', NULL, 'গৌরীপুর প্রেস ক্লাবের প্রতিষ্ঠাবার্ষিকী উদ্‌যাপন', 'ময়মনসিংহের গৌরীপুর উপজেলা প্রেস ক্লাবের উদ্যোগে সংগঠনের প্রথম প্রতিষ্ঠাবার্ষিকী উপলক্ষে গতকাল বর্ণিল শোভাযাত্রা, কেককাটা ও আলোচনা সভা অনুষ্ঠিত হয়', 'ময়মনসিংহের গৌরীপুর উপজেলা প্রেস ক্লাবের উদ্যোগে সংগঠনের প্রথম প্রতিষ্ঠাবার্ষিকী উপলক্ষে গতকাল বর্ণিল শোভাযাত্রা, কেককাটা ও আলোচনা সভা অনুষ্ঠিত হয়\nময়মনসিংহের গৌরীপুর উপজেলা প্রেস ক্লাবের উদ্যোগে সংগঠনের প্রথম প্রতিষ্ঠাবার্ষিকী উপলক্ষে গতকাল বর্ণিল শোভাযাত্রা, কেককাটা ও আলোচনা সভা অনুষ্ঠিত হয়। অনুষ্ঠানের শুরুতেই মহান মুক্তিযুদ্ধে আত্মত্যাগকারী সব শহীদ ও জুলাই বিপ্লব শহীদদের স্মরণ এবং গণতন্ত্রের মুক্তি ও স্বাধীন সাংবাদিকতা করতে গিয়ে যারা আত্মোৎসর্গ করেছেন তাদের বিদেহী আত্মার মাগফিরাত কামনা করা হয়।অনুষ্ঠানে প্রধান অতিথির বক্তব্য রাখেন উপজেলা নির্বাহী অফিসার মো. শাকিল আহমেদ। সভাপতিত্ব করেন দৈনিক যুগান্তরের গৌরীপুর প্রতিনিধি ও সংগঠনের আহ্বায়ক মো. রইছ উদ্দিন। সঞ্চালনা করেন যুগ্ম আহ্বায়ক মোশারফ হোসেন সোহেল। বিশেষ অতিথির বক্তব্য রাখেন উপজেলা একাডেমিক সুপারভাইজার কমল কুমার রায়, কালের কণ্ঠের আঞ্চলিক প্রতিনিধি আলম ফরাজী, ময়মনসিংহ উত্তর জেলা যুবদলের সদস্য মোস্তাফিজুর রহমান হানিফ, বৈষম্যবিরোধী ছাত্র আন্দোলনের সমন্বয়ক বাহালুল মুনশী, শামছুজ্জামান দুর্জয়, উপজেলা ছাত্রদলের সদস্য সচিব মনিরুজ্জামান মানিক, স্বদেশ বিচিত্রা’র স্টাফ রিপোর্টার এসএম জোবায়ের হোসাইন।এ ছাড়া বক্তব্য রাখেন উপজেলা প্রেস ক্লাবের যুগ্ম আহ্বায়ক দৈনিক আজকের প্রভাতের প্রতিনিধি জহিরুল হুদা লিটন, দৈনিক বাংলাদেশের আলো’র প্রতিনিধি সেলিম আল রাজ, দৈনিক আজকালের খবরের প্রতিনিধি মোস্তাফিজুর রহমান বুরহান, কার্যকরী সদস্য, দৈনিক মানবজমিন প্রতিনিধি শামীম হোসেন, দৈনিক সংগ্রামের প্রতিনিধি মোখলেছুর রহমান, এসটি বাংলা টিভি’র প্রতিনিধি আব্দুর রউফ দুদু, দৈনিক আজকের সংবাদের প্রতিনিধি শ্যামল ঘোষ, দৈনিক সমবাংলার প্রতিনিধি মো. খায়রুল ইসলাম, আজকের শতাব্দীর প্রতিনিধি আশিকুর রহমান রাজিব, দৈনিক দিগন্তবাংলার প্রতিনিধি তৌহিদুল আমিন তুহিন, দৈনিক বাহাদুরের তাসাদদুল করিম, এসটিবাংলা টিভি’র ঈশ্বরগঞ্জ প্রতিনিধি শামীম আনোয়ার, কবি শামীমা খানম মীনা।', '2024-12-03 00:00:00', '2024-12-03 03:51:12', '[]', 1813, 'bd3052ab71eb868cad69bcbcb0793901');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746236, 2268, 'https://mzamin.com/news.php?news=138342', 'https://mzamin.com/news.php?news=138342', 'সংবাদ', NULL, 'সিলেট চেম্বারে ৫ই ডিসেম্বরের মধ্যে প্রশাসক নিয়োগের দাবি', 'সিলেটের ব্যবসায়ীদের শীর্ষ সংগঠন সিলেট চেম্বার অব কমার্সে আওয়ামী স্বৈরাচারের মদতপুষ্ট পরিষদ বাতিল করে প্রতিনিধিত্বশীল পরিচালনা পর্যদ গঠনের দাবি জানিয়েছেন সর্বস্তরের ব্যবসায়ী', 'সিলেটের ব্যবসায়ীদের শীর্ষ সংগঠন সিলেট চেম্বার অব কমার্সে আওয়ামী স্বৈরাচারের মদতপুষ্ট পরিষদ বাতিল করে প্রতিনিধিত্বশীল পরিচালনা পর্যদ গঠনের দাবি জানিয়েছেন সর্বস্তরের ব্যবসায়ী\nসিলেটের ব্যবসায়ীদের শীর্ষ সংগঠন সিলেট চেম্বার অব কমার্সে আওয়ামী স্বৈরাচারের মদতপুষ্ট পরিষদ বাতিল করে প্রতিনিধিত্বশীল পরিচালনা পর্যদ গঠনের দাবি জানিয়েছেন সর্বস্তরের ব্যবসায়ী। তারা ৫ই ডিসেম্বরের মধ্যে এ দাবি মেনে নিয়ে প্রশাসক নিয়োগ করা না হলে ৮ ডিসেম্বর সিলেট চেম্বারের সামনে মানববন্ধন কর্মসূচি পালন এবং পরবর্তী কর্মসূচি ঘোষণার হুঁশিয়ারি দেন। সোমবার সিলেট নগরীর একটি রেস্টুরেন্টে আয়োজিত সংবাদ সম্মেলনে সর্বস্তরের ব্যবসায়ীদের পক্ষে এ দাবি জানান চেম্বারের সাবেক পরিচালক আমিরুজ্জামান চৌধুরী দুলু। সংবাদ সম্মেলনে লিখিত বক্তব্যে তিনি বলেন, বিগত ফ্যাসিস্ট সরকারের আমলে দি সিলেট চেম্বার অব কমার্স অ্যান্ড ইন্ডাস্ট্রি ব্যবসায়ীদের স্বার্থে তেমন কোনো কার্যক্রম পরিচালনা করেনি। বরং রাজনৈতিক মদদপুষ্ট কিছু অসৎ ব্যবসায়ী সিলেট চেম্বারের নেতৃত্বে আসীন হয়ে চেম্বারকে দলীয় প্রতিষ্ঠানে পরিণত করে। এ ধারাবাহিকতার ২০২২-২৩ সালে পরিচালনা পরিষদের মেয়াদোত্তীর্ণ হওয়ার পর প্রথমে বাণিজ্য মন্ত্রণালয়ে আবেদনক্রমে নির্বাচন করতে পরিষদের মেয়াদ বৃদ্ধি করা হয়। পরে বাণিজ্য সংগঠনের বিধিমালা লঙ্ঘন করে স্থানীয় আওয়ামী লীগ নেতাদের যোগসাজশে গোপনীয়ভাবে নির্বাচন দেখিয়ে ভোটার ও প্রার্থীদের ভয়ভীতি প্রদর্শন করে ২০২৪-২৫ মেয়াদের জন্য পরিচালনা পরিষদ ঘোষণা করা হয়। সিলেটের ব্যবসায়ী সমাজ এই বিতর্কিত কর্মকাণ্ডের প্রতিবাদ করলে পরিচালনা পরিষদের ২০ পরিচালকের মধ্যে সহ-সভাপতিসহ ৫ পরিচালক এ পরিষদ থেকে পদত্যাগ করেন। বৈষম্যবিরোধী আন্দোলনে ছাত্রদের ওপর সন্ত্রাসী কর্মকাণ্ডের অভিযোগে চেম্বার সভাপতি তাহমিন আহমদসহ আরও ৫-৬ জন পরিচালকের ওপর মামলা হয়। এ মামলার আসামিরা বর্তমানে পলাতক থেকেও নানা ষড়যন্ত্রে লিপ্ত রয়েছেন। সংবাদ সম্মেলনে আমিতুজ্জামান চৌধুরী দুলু অভিযোগ করে বলেন, স্বৈরাচারের দোসররা গোপনীয়ভাবে পরিচালনা পরিষদ নিজেরা পুনর্গঠন করে একজন পরিচালককে সভাপতির দায়িত্ব দিয়েছেন।যিনি স্থায়ীভাবে বসবাসের জন্য যুক্তরাষ্ট্রে অবস্থান করছেন। বর্তমানে একজন সিনিয়র পরিচালককে ভারপ্রাপ্ত সভাপতি বানিয়ে চেম্বার কার্যক্রম পরিচালনা করা হচ্ছে। সংবাদ সম্মেলনে নেতারা বলেন, আমরা সিলেটের ঐতিহ্যবাহী এই সংগঠন নিয়ে কোনো ধরনের ষড়যন্ত্র মেনে নিতে পারি না। আমরা ব্যবসায়ীরা ঐক্যবদ্ধভাবে সব যড়যন্ত্র প্রতিহত করতে প্রস্তুত রয়েছি। নির্বাচন আয়োজনের জন্য একজন প্রশাসক নিয়োগের কোনো বিকল্প নেই বলে সিলেটের ব্যবসায়ীরা মনে করেন। এ ব্যাপারে সংশ্লিষ্ট সবার সহযেগিতা কামনা করেন। সংবাদ সম্মেলনে ছিলেন সিলেট চেম্বারের সাবেক সভাপতি ও এফবিসিসিআই’র সাবেক পরিচালক খন্দকার সিপার আহমদ।', '2024-12-03 00:00:00', '2024-12-03 03:51:13', '[]', 1813, '9e83511cb5eb4936688790925e944bd5');
INSERT INTO `news` (`id`, `website_id`, `request_url`, `response_url`, `category1`, `category2`, `title`, `abstract`, `body`, `pub_time`, `cole_time`, `images`, `language_id`, `md5`) VALUES (21746237, 2268, 'https://mzamin.com/news.php?news=138341', 'https://mzamin.com/news.php?news=138341', 'সংবাদ', NULL, 'ফুলছড়িতে বিএনপি কার্যালয়ে ককটেল বিস্ফোরণ: যুবদল নেতা আহত', 'গাইবান্ধার ফুলছড়ি উপজেলায় ইউনিয়ন বিএনপি’র কার্যালয়ে ককটেল বিস্ফোরণ ও হামলার ঘটনা ঘটেছে', 'গাইবান্ধার ফুলছড়ি উপজেলায় ইউনিয়ন বিএনপি’র কার্যালয়ে ককটেল বিস্ফোরণ ও হামলার ঘটনা ঘটেছে\nগাইবান্ধার ফুলছড়ি উপজেলায় ইউনিয়ন বিএনপি’র কার্যালয়ে ককটেল বিস্ফোরণ ও হামলার ঘটনা ঘটেছে। রোববার রাত সাড়ে ৭টার দিকে এ ঘটনা ঘটে। এতে ওই ইউনিয়নের যুবদলের সদস্য সচিব মো. শাহজাহান (৩৭) আহত হয়েছেন। পরে তাকে দ্রুত উদ্ধার করে স্থানীয় হাসপাতালে ভর্তি করা হয়।স্থানীয় সূত্রে জানা গেছে, হামলার সময় সন্ত্রাসী বাহিনী কার্যালয়ে ককটেল বিস্ফোরণ ঘটিয়ে আতঙ্ক সৃষ্টি করে এবং দলীয় কর্মীদের লক্ষ্য করে হামলা চালায়।ফজলুপুর ইউনিয়ন বিএনপির সভাপতি মিজানুর রহমান মিলন এই ঘটনার তীব্র নিন্দা জানিয়ে বলেন, আওয়ামী সন্ত্রাসীরা রাজনৈতিক প্রতিহিংসার কারণে এ হামলা চালিয়েছে। আমরা এই ঘটনার সুষ্ঠু তদন্ত ও দোষীদের দৃষ্টান্তমূলক শাস্তি দাবি করছি।', '2024-12-03 00:00:00', '2024-12-03 03:51:14', '[]', 1813, 'eab6cdaec850029a775b787e6a762079');
